import { Component, OnInit } from '@angular/core';
import { Task } from '../models/task.model';
import { TeamMember } from '../models/team-member.model';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {
  // Data for Task Summary
  totalTasks: number = 45;
  tasksCompleted: number = 20;
  overdueTasks: number = 5;
  progress: number = (this.tasksCompleted / this.totalTasks) * 100;

  // Task Status Data
  taskStatus: Task[] = [
    { taskTitle: 'Task 1', assignedTo: 'John', status: 'Completed', priority: 'High', dueDate: new Date('2025-04-10') },
    { taskTitle: 'Task 2', assignedTo: 'Alice', status: 'In Progress', priority: 'Low', dueDate: new Date('2025-04-12') },
    { taskTitle: 'Task 3', assignedTo: 'Bob', status: 'Completed', priority: 'High', dueDate: new Date('2025-04-15') },
    // Add other tasks here...
  ];

  // Filtered Tasks for Task Status
  filteredTasks: Task[] = this.taskStatus;
  selectedStatus: string = 'All';

  // Team Performance Data
  teamData: TeamMember[] = [
    { id: 1, name: 'John', role: 'Developer', tasksAssigned: 10, tasksCompleted: 8, progress: 80 },
    { id: 2, name: 'Alice', role: 'Developer', tasksAssigned: 8, tasksCompleted: 6, progress: 60 },
    { id: 3, name: 'Bob', role: 'Manager', tasksAssigned: 12, tasksCompleted: 10, progress: 90 },
    // Add other team members here...
  ];

  filteredTeamData: TeamMember[] = this.teamData;
  selectedTeamMember: string = 'All';

  // Upcoming Deadlines
  upcomingDeadlines = [
    { name: 'Task 1', deadline: '2025-04-10' },
    { name: 'Task 2', deadline: '2025-04-12' },
    { name: 'Task 3', deadline: '2025-04-15' },
    // Add other deadlines here...
  ];

  filteredDeadlines = [...this.upcomingDeadlines];
  selectedDeadline: string = 'All';

  // Columns for the task status table
  displayedColumns: string[] = ['taskTitle', 'assignedTo', 'status', 'priority', 'dueDate'];
  displayedColumnsDeadlines: string[] = ['task', 'deadline'];

  constructor() {}

  ngOnInit(): void {}

  // Filter tasks based on selected status
  filterTasks(status: string): void {
    if (status === 'All') {
      this.filteredTasks = [...this.taskStatus];
    } else {
      this.filteredTasks = this.taskStatus.filter(task => task.status === status);
    }
  }

  // Filter team performance by member
  filterTeamMember(member: string): void {
    if (member === 'All') {
      this.filteredTeamData = [...this.teamData];
    } else {
      this.filteredTeamData = this.teamData.filter(team => team.name === member);
    }
  }

  // Filter upcoming deadlines by date
  filterDeadlines(date: string): void {
    if (date === 'All') {
      this.filteredDeadlines = [...this.upcomingDeadlines];
    } else {
      this.filteredDeadlines = this.upcomingDeadlines.filter(deadline => deadline.deadline === date);
    }
  }
}
<div class="dashboard-container">
  <!-- Project Overview -->
  <mat-card class="dashboard-card">
    <mat-card-header>
      <mat-card-title>Project Overview</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <div class="progress-bar">
        <mat-progress-bar mode="determinate" [value]="progress"></mat-progress-bar>
      </div>
      <p>{{ progress | number: '1.2-2' }}% Completed</p>
    </mat-card-content>
  </mat-card>

  <!-- Task Status Table -->
  <mat-card class="task-status-card">
    <mat-card-header>
      <mat-card-title>Task Status</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <!-- Task Status Filter Dropdown -->
      <mat-form-field appearance="fill">
        <mat-label>Filter by Status</mat-label>
        <mat-select [(value)]="selectedStatus" (selectionChange)="filterTasks(selectedStatus)">
          <mat-option value="All">All Tasks</mat-option>
          <mat-option value="In Progress">In Progress</mat-option>
          <mat-option value="Completed">Completed</mat-option>
          <mat-option value="Overdue">Overdue</mat-option>
        </mat-select>
      </mat-form-field>

      <table mat-table [dataSource]="filteredTasks" class="mat-elevation-z8">
        <ng-container matColumnDef="taskTitle">
          <th mat-header-cell *matHeaderCellDef> Task </th>
          <td mat-cell *matCellDef="let task"> {{task.taskTitle}} </td>
        </ng-container>

        <ng-container matColumnDef="status">
          <th mat-header-cell *matHeaderCellDef> Status </th>
          <td mat-cell *matCellDef="let task"> {{task.status}} </td>
        </ng-container>

        <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
      </table>
    </mat-card-content>
  </mat-card>

  <!-- Task Summary -->
  <mat-card class="task-summary-card">
    <mat-card-header>
      <mat-card-title>Task Summary</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <div class="task-summary">
        <div class="task-summary-box">Total Tasks: {{ totalTasks }}</div>
        <div class="task-summary-box">Tasks Completed: {{ tasksCompleted }}</div>
        <div class="task-summary-box">Overdue Tasks: {{ overdueTasks }}</div>
      </div>
    </mat-card-content>
  </mat-card>

  <!-- Team Performance -->
  <mat-card class="team-performance-card">
    <mat-card-header>
      <mat-card-title>Team Performance</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <!-- Team Member Filter Dropdown -->
      <mat-form-field appearance="fill">
        <mat-label>Filter by Team Member</mat-label>
        <mat-select [(value)]="selectedTeamMember" (selectionChange)="filterTeamMember(selectedTeamMember)">
          <mat-option value="All">All Members</mat-option>
          <mat-option value="John">John</mat-option>
          <mat-option value="Alice">Alice</mat-option>
          <mat-option value="Bob">Bob</mat-option>
        </mat-select>
      </mat-form-field>

      <ol class="list">
        <li *ngFor="let member of filteredTeamData">
          {{ member.name }}: {{ member.tasksAssigned }} tasks assigned, {{ member.tasksCompleted }} tasks completed
        </li>
      </ol>
    </mat-card-content>
  </mat-card>

  <!-- Upcoming Deadlines -->
  <mat-card class="upcoming-deadlines-card">
    <mat-card-header>
      <mat-card-title>Upcoming Deadlines</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <!-- Deadline Filter Dropdown -->
      <mat-form-field appearance="fill">
        <mat-label>Filter by Deadline</mat-label>
        <mat-select [(value)]="selectedDeadline" (selectionChange)="filterDeadlines(selectedDeadline)">
          <mat-option value="All">All Dates</mat-option>
          <mat-option value="2025-04-10">April 10</mat-option>
          <mat-option value="2025-04-12">April 12</mat-option>
          <mat-option value="2025-04-15">April 15</mat-option>
        </mat-select>
      </mat-form-field>

      <table mat-table [dataSource]="filteredDeadlines" class="mat-elevation-z8">
        <ng-container matColumnDef="task">
          <th mat-header-cell *matHeaderCellDef> Task </th>
          <td mat-cell *matCellDef="let task"> {{task.name}} </td>
        </ng-container>

        <ng-container matColumnDef="deadline">
          <th mat-header-cell *matHeaderCellDef> Deadline </th>
          <td mat-cell *matCellDef="let task"> {{task.deadline}} </td>
        </ng-container>

        <tr mat-header-row *matHeaderRowDef="displayedColumnsDeadlines"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumnsDeadlines;"></tr>
      </table>
    </mat-card-content>
  </mat-card>
</div>
