import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Task } from '../models/task.model'; // Import the Task interface

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.scss']
})
export class TaskListComponent implements OnInit {

  // Task Data
  tasks: Task[] = [
    { taskTitle: 'Task 1', assignedTo: 'John', status: 'Not Started', priority: 'High', dueDate: new Date('2025-04-10') },
    { taskTitle: 'Task 2', assignedTo: 'Alice', status: 'In Progress', priority: 'Medium', dueDate: new Date('2025-04-12') },
    { taskTitle: 'Task 3', assignedTo: 'Bob', status: 'Completed', priority: 'Low', dueDate: new Date('2025-04-15') },
    // More tasks
  ];

  // Filter Options
  taskStatuses = ['Not Started', 'In Progress', 'Completed'];
  priorities = ['High', 'Medium', 'Low'];
  teamMembers = ['John', 'Alice', 'Bob'];

  filterStatus = '';
  filterPriority = '';
  filterAssigned = '';
  filterDate: Date | null = null;  // Added Date Filter

  // Task Summary (number of tasks per status)
  taskSummary = {
    notStarted: 0,
    inProgress: 0,
    completed: 0
  };

  // Filtered Tasks based on filters
  filteredTasks = this.tasks;

  // Columns for mat-table
  displayedColumns: string[] = ['taskTitle', 'assignedTo', 'status', 'priority', 'dueDate', 'action'];

  constructor(private router: Router) {}

  ngOnInit(): void {
    this.updateTaskSummary();
    this.applyFilters();
  }

  // Update task summary counts
  updateTaskSummary() {
    this.taskSummary.notStarted = this.tasks.filter(task => task.status === 'Not Started').length;
    this.taskSummary.inProgress = this.tasks.filter(task => task.status === 'In Progress').length;
    this.taskSummary.completed = this.tasks.filter(task => task.status === 'Completed').length;
  }

  // Apply filters to tasks
  applyFilters() {
    this.filteredTasks = this.tasks.filter(task => {
      return (
        (this.filterStatus ? task.status === this.filterStatus : true) &&
        (this.filterPriority ? task.priority === this.filterPriority : true) &&
        (this.filterAssigned ? task.assignedTo === this.filterAssigned : true) &&
        (this.filterDate ? task.dueDate <= this.filterDate : true) // Apply date filter
      );
    });
  }

  // Navigate to Task Detail page
  goToTaskDetail(task: Task) {
