import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  isSidebarCollapsed = false;
  isDashboardVisible = false; // Initially, the dashboard is hidden

  showDashboard() {
    this.isDashboardVisible = true; // Set to true when the "Get Started" button is clicked
  }
  navigateToDashboard() {
    this.router.navigate(['/dashboard']); // This will navigate to the dashboard when clicked
  }
}
<!-- Show only the Welcome section initially -->
<div *ngIf="!isDashboardVisible" class="welcome-container">
  <div class="welcome-content">
    <h1>Project Management Dashboard</h1>
    <p>
      Our Project Management Dashboard is specifically designed to help project managers streamline workflows,
      track project timelines, assign tasks, and keep the team on track. It provides a detailed overview of
      project progress, task statuses, and team performance.
    </p>
    <button (click)="navigateToDashboard()">Get Started</button>
  </div>
</div>

<!-- Show header and dashboard content after "Get Started" is clicked -->
<div *ngIf="isDashboardVisible" class="app-container">
  <app-header></app-header> <!-- The header -->
  <app-sidebar></app-sidebar> <!-- Sidebar -->
  <div class="content">
    <router-outlet></router-outlet> <!-- Routed content will go here -->
  </div>
</div>
