import { Component, OnInit } from '@angular/core';
import { TaskDataService } from '../task-data.service'; // Import the service

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.scss']
})
export class TaskListComponent implements OnInit {

  tasks: any[] = [];
  filteredTasks: any[] = [];

  constructor(private taskDataService: TaskDataService) {}

  ngOnInit(): void {
    this.tasks = this.taskDataService.getTasks();
    this.filteredTasks = [...this.tasks];
  }

  editTask(task: any) {
    this.taskDataService.editTask(task);
  }

  deleteTask(taskTitle: string) {
    this.taskDataService.deleteTask(taskTitle);
    this.filteredTasks = this.taskDataService.getTasks();
  }

  addTask(newTask: any) {
    this.taskDataService.addTask(newTask);
    this.filteredTasks = this.taskDataService.getTasks();
  }
}
<mat-card *ngFor="let task of filteredTasks">
  <mat-card-header>
    <mat-card-title>{{ task.taskTitle }}</mat-card-title>
  </mat-card-header>
  <mat-card-content>
    <p><strong>Assigned To:</strong> {{ task.assignedTo }}</p>
    <p><strong>Status:</strong> {{ task.status }}</p>
    <p><strong>Priority:</strong> {{ task.priority }}</p>
    <p><strong>Due Date:</strong> {{ task.dueDate | date:'shortDate' }}</p>
  </mat-card-content>
  <mat-card-actions>
    <button mat-button (click)="editTask(task)">Edit</button>
    <button mat-button (click)="deleteTask(task.taskTitle)">Delete</button>
  </mat-card-actions>
</mat-card>
