import { Component, OnInit, AfterViewInit, ElementRef, ViewChild } from '@angular/core';
import { Chart, registerables } from 'chart.js/auto';
import { CommonModule, DatePipe } from '@angular/common';
import { MatProgressBarModule } from '@angular/material/progress-bar';
import { MatIconModule } from '@angular/material/icon';
import { MatButtonModule } from '@angular/material/button';
import { ThemeToggleComponent } from '../shared/theme-toggle/theme-toggle.component';
import { FirestoreService } from '../services/firestore.service';
import { Task } from '../models/task.model';
import { Project } from '../models/project.model';
import { take } from 'rxjs';

Chart.register(...registerables);

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss'],
  standalone: true,
  imports: [
    CommonModule,
    MatProgressBarModule,
    MatIconModule,
    MatButtonModule,
    ThemeToggleComponent
  ],
  providers: [DatePipe]
})
export class DashboardComponent implements OnInit, AfterViewInit {
  @ViewChild('donutChart') donutChartRef!: ElementRef;
  @ViewChild('performanceChart') performanceChartRef!: ElementRef;

  donutChart!: Chart;
  performanceChart!: Chart;

  tasks: Task[] = [];
  projects: Project[] = [];
  totalTasks = 0;
  tasksCompleted = 0;
  tasksInProgress = 0;
  tasksNotStarted = 0;
  overdueTasks = 0;

  totalProjects = 0;
  projectsCompleted = 0;
  projectsInProgress = 0;
  projectsNotStarted = 0;

  overallProgress = 0;
  upcomingDeadlines: { name: string; deadline: Date; type: 'Task' | 'Project' }[] = [];

  donutChartData: number[] = [];
  donutChartLabels: string[] = ['Completed', 'In Progress', 'Not Started'];
  performanceChartData: number[] = [];
  performanceChartLabels: string[] = [];

  currentProjectName: string = 'N/A';
  currentProjectProgress: number = 0;
  currentProjectDaysRemaining: number = 0;

  showAllProjects = false;
  showAllTasks = false;

  constructor(private datePipe: DatePipe, private firestoreService: FirestoreService) {}

  ngOnInit(): void {
    this.fetchData();
  }

  fetchData(): void {
    this.firestoreService.getTasks().pipe(take(1)).subscribe(tasks => {
      this.tasks = tasks;
      this.calculateAllSummaries();
      this.prepareUpcomingDeadlines();
      this.prepareChartData();
    });

    this.firestoreService.getProjects().pipe(take(1)).subscribe(projects => {
      this.projects = projects.map(project => {
        let progress = 0;
        if (project.status === 'Completed') progress = 100;
        else if (project.status === 'In Progress') progress = 50;
        return { ...project, progress };
      });
      this.prepareProjectOverview();
    });
  }

  ngAfterViewInit() {
    setTimeout(() => {
      this.initializeDonutChart();
      this.initializePerformanceChart();
    }, 0);
  }

  calculateAllSummaries(): void {
    const now = new Date();
    this.totalTasks = this.tasks.length;
    this.tasksCompleted = this.tasks.filter(t => t.status === 'Completed').length;
    this.tasksInProgress = this.tasks.filter(t => t.status === 'In Progress').length;
    this.tasksNotStarted = this.tasks.filter(t => t.status === 'Not Started').length;
    this.overdueTasks = this.tasks.filter(t => t.status !== 'Completed' && t.dueDate < now).length;
    this.overallProgress = this.totalTasks > 0 ? Math.round((this.tasksCompleted / this.totalTasks) * 100) : 0;
  }

  prepareUpcomingDeadlines(): void {
    this.upcomingDeadlines = this.tasks
      .map(t => ({ name: t.name, deadline: t.dueDate, type: 'Task' as const }))
      .filter(d => d.deadline >= new Date())
      .sort((a, b) => a.deadline.getTime() - b.deadline.getTime());
  }

  prepareChartData(): void {
    this.donutChartData = [
      this.tasksCompleted,
      this.tasksInProgress,
      this.tasksNotStarted
    ];

    this.performanceChartLabels = ['Mar 1', 'Mar 8', 'Mar 15', 'Mar 22', 'Mar 29', 'Apr 5'];
    this.performanceChartData = [1, 3, 2, 5, 4, 6]; // Optional: Replace with weekly aggregation
  }

  prepareProjectOverview(): void {
    this.totalProjects = this.projects.length;
    this.projectsCompleted = this.projects.filter(p => p.status === 'Completed').length;
    this.projectsInProgress = this.projects.filter(p => p.status === 'In Progress').length;
    this.projectsNotStarted = this.projects.filter(p => p.status === 'Not Started').length;
  }

  private initializeDonutChart() {
    if (!this.donutChartRef || this.donutChartData.length === 0) return;
    const canvas = this.donutChartRef.nativeElement;
    const ctx = canvas.getContext('2d');

    if (this.donutChart) this.donutChart.destroy();

    this.donutChart = new Chart(ctx, {
      type: 'doughnut',
      data: {
        labels: this.donutChartLabels,
        datasets: [{
          data: this.donutChartData,
          backgroundColor: ['#4ade80', '#60a5fa', '#a78bfa'],
          borderWidth: 0
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        cutout: '75%',
        plugins: { legend: { display: false }, tooltip: { enabled: true } }
      }
    });
  }

  private initializePerformanceChart() {
    if (!this.performanceChartRef || this.performanceChartData.length === 0) return;
    const canvas = this.performanceChartRef.nativeElement;
    const ctx = canvas.getContext('2d');

    if (this.performanceChart) this.performanceChart.destroy();

    this.performanceChart = new Chart(ctx, {
      type: 'line',
      data: {
        labels: this.performanceChartLabels,
        datasets: [{
          label: 'Tasks Completed Weekly',
          data: this.performanceChartData,
          fill: false,
          borderColor: '#60a5fa',
          pointBackgroundColor: '#60a5fa',
          pointBorderColor: '#60a5fa',
          pointRadius: 4,
          tension: 0.3
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
          y: { beginAtZero: true, ticks: { color: '#a0aec0' }, grid: { color: 'rgba(255,255,255,0.1)' } },
          x: { ticks: { color: '#a0aec0' }, grid: { display: false } }
        },
        plugins: { legend: { display: false } }
      }
    });
  }

  toggleProjects(): void {
    this.showAllProjects = !this.showAllProjects;
  }

  toggleTasks(): void {
    this.showAllTasks = !this.showAllTasks;
  }
}
<app-theme-toggle class="theme-toggle-icon"></app-theme-toggle>

<div class="dashboard-container-original">

  <!-- Left Column -->
  <div class="left-column">
    <!-- Task Status Card -->
    <div class="dashboard-card task-status-card-original">
      <h2>Task Status</h2>
      <div class="donut-chart-container">
        <canvas #donutChart></canvas>
      </div>
      <div class="status-legend-original">
        <div class="status-item">
          <span class="status-dot completed"></span>
          <span class="status-text">Completed</span>
          <span class="status-count">{{ tasksCompleted }}</span>
        </div>
        <div class="status-item">
          <span class="status-dot in-progress"></span>
          <span class="status-text">In Progress</span>
          <span class="status-count">{{ tasksInProgress }}</span>
        </div>
        <div class="status-item">
          <span class="status-dot not-started"></span>
          <span class="status-text">Not Started</span>
          <span class="status-count">{{ tasksNotStarted }}</span>
        </div>
      </div>
    </div>

    <!-- Task Summary Card -->
    <div class="dashboard-card task-summary-card-original">
      <h2>Task Summary</h2>
      <div class="summary-list">
        <div class="summary-item">
          <span>Total Tasks</span>
          <span>{{ totalTasks }}</span>
        </div>
        <div class="summary-item">
          <span>Completed</span>
          <span>{{ tasksCompleted }}</span>
        </div>
        <div class="summary-item">
          <span>In Progress</span>
          <span>{{ tasksInProgress }}</span>
        </div>
        <div class="summary-item">
          <span>Not Started</span>
          <span class="overdue">{{ tasksNotStarted }}</span>
        </div>
      </div>
    </div>
  </div>

  <!-- Right Column -->
  <div class="right-column">
    <!-- Project Overview Card -->
    <div class="dashboard-card project-overview-card-original">
      <h2>Project Overview</h2>
      <div *ngFor="let project of (showAllProjects ? projects : projects.slice(0, 2))">
        <h3>{{ project.name }}</h3>
        <div class="progress-bar-original">
          <div class="progress-fill" [style.width.%]="project.progress"></div>
        </div>
        <div class="progress-percentage-line">{{ project.progress }}%</div>

        <div class="progress-info">
          <span *ngIf="showAllProjects" class="days-remaining">{{ project.dueDate | date:'MMM d, yyyy' }}</span>
        </div>
      </div>
      <div class="show-more-container">
        <button mat-stroked-button (click)="toggleProjects()">
          <mat-icon>{{ showAllProjects ? 'expand_less' : 'expand_more' }}</mat-icon>
          {{ showAllProjects ? 'Show Less' : 'Show More' }}
        </button>
      </div>
    </div>

    <!-- Team Performance Card -->
    <div class="dashboard-card team-performance-card-original">
      <h2>Team Performance</h2>
      <div class="performance-chart-container">
        <canvas #performanceChart></canvas>
      </div>
    </div>

    <!-- Upcoming Deadlines Card -->
    <div class="dashboard-card upcoming-deadlines-card-original">
      <h2>Upcoming Deadlines</h2>
      <div class="deadline-list-original">
        <div class="deadline-item" *ngFor="let deadline of (showAllTasks ? upcomingDeadlines : upcomingDeadlines.slice(0, 3))">
          <span class="item-name">{{ deadline.name }}</span>
          <span class="due-date">{{ deadline.deadline | date:'MMM d, yyyy' }}</span>
        </div>
        <div *ngIf="upcomingDeadlines.length === 0" class="no-items">
          No upcoming deadlines.
        </div>
      </div>
      <div *ngIf="upcomingDeadlines.length > 3" class="show-more-container">
        <button mat-stroked-button (click)="toggleTasks()">
          <mat-icon>{{ showAllTasks ? 'expand_less' : 'expand_more' }}</mat-icon>
          {{ showAllTasks ? 'Show Less' : 'Show More' }}
        </button>
      </div>
    </div>
  </div>

</div>
