import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { FormsModule } from '@angular/forms';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatDatepickerModule } from '@angular/material/datepicker';
import { MatNativeDateModule } from '@angular/material/core';
import { MatSelectModule } from '@angular/material/select';
import { DatePipe } from '@angular/common';
import { FirestoreService } from '../services/firestore.service';
import { Task } from '../models/task.model';
import { take } from 'rxjs';
import { Timestamp } from '@angular/fire/firestore'; // ✅ Needed to detect Timestamp type
import { RefreshService } from 'src/app/services/refresh.service';

@Component({
  selector: 'app-tasks',
  templateUrl: './tasks.component.html',
  styleUrls: ['./tasks.component.scss'],
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    FormsModule,
    MatButtonModule,
    MatIconModule,
    MatFormFieldModule,
    MatInputModule,
    MatDatepickerModule,
    MatNativeDateModule,
    MatSelectModule
    ],
  providers: [DatePipe]
})
export class TasksComponent implements OnInit {
  tasks: Task[] = [];
  filteredTasks: Task[] = [];
  taskForm!: FormGroup;
  isEditing = false;
  editingTaskId: string | null = null;
  taskStatuses: string[] = ['Not Started', 'In Progress', 'Completed'];
  assigneeList: string[] = [];
  selectedAssignee: string = 'All';
  selectedDate: Date | null = null;
  showAllTasks = false;

  constructor(
    private fb: FormBuilder,
    private datePipe: DatePipe,
    private firestoreService: FirestoreService,
    private refreshService: RefreshService
  ) {}

  ngOnInit(): void {
    this.taskForm = this.fb.group({
      name: ['', Validators.required],
      assignee: ['', Validators.required],
      dueDate: [null, Validators.required],
      status: ['Not Started', Validators.required]
    });

    this.loadData();

    this.refreshService.refresh$.subscribe(() => {
      this.loadData();
    });
  }

  loadTasks(): void {
    this.firestoreService.getTasks().pipe(take(1)).subscribe(tasks => {
      this.tasks = tasks;
      this.updateAssigneeList();
      this.applyFilters();
    });
  }

  updateAssigneeList(): void {
    const assignees = new Set(this.tasks.map(task => task.assignee));
    this.assigneeList = ['All', ...Array.from(assignees).sort()];
  }

  applyFilters(): void {
    let tempTasks = [...this.tasks];
    if (this.selectedAssignee && this.selectedAssignee !== 'All') {
      tempTasks = tempTasks.filter(task => task.assignee === this.selectedAssignee);
    }
    if (this.selectedDate) {
      const filterDate = new Date(this.selectedDate);
      filterDate.setHours(0, 0, 0, 0);
      const filterTime = filterDate.getTime();
      tempTasks = tempTasks.filter(task => {
        const taskDueDate = this.convertToDate(task.dueDate);
        taskDueDate.setHours(0, 0, 0, 0);
        return taskDueDate.getTime() === filterTime;
      });
    }
    this.filteredTasks = tempTasks.sort((a, b) =>
      this.convertToDate(a.dueDate).getTime() - this.convertToDate(b.dueDate).getTime()
    );
  }

  clearDateFilter(): void {
    this.selectedDate = null;
    this.applyFilters();
  }

  showAddTaskForm(): void {
    this.isEditing = true;
    this.editingTaskId = null;
    this.taskForm.reset({ status: 'Not Started' });
  }

  editTask(task: Task): void {
    this.isEditing = true;
    this.editingTaskId = task.id || null;
    this.taskForm.setValue({
      name: task.name,
      assignee: task.assignee,
      dueDate: this.convertToDate(task.dueDate), // 🔁 ensure compatibility
      status: task.status
    });
  }

  saveTask(): void {
    if (this.taskForm.invalid) return;
    const formValue = this.taskForm.value;
    const newTask: Task = { ...formValue };

    if (this.editingTaskId) {
      this.firestoreService.updateTask(this.editingTaskId, newTask).pipe(take(1)).subscribe(() => {
        const index = this.tasks.findIndex(t => t.id === this.editingTaskId);
        if (index > -1) this.tasks[index] = { ...this.tasks[index], ...newTask };
        this.afterChange();
      });
    } else {
      this.firestoreService.addTask(newTask).pipe(take(1)).subscribe(docRef => {
        this.tasks.push({ id: docRef.id, ...newTask });
        this.afterChange();
      });
    }
  }

  deleteTask(taskId: string): void {
    this.firestoreService.deleteTask(taskId).pipe(take(1)).subscribe(() => {
      this.tasks = this.tasks.filter(t => t.id !== taskId);
      this.afterChange();
    });
  }

  afterChange(): void {
    this.updateAssigneeList();
    this.applyFilters();
    this.cancelEdit();
  }

  cancelEdit(): void {
    this.isEditing = false;
    this.editingTaskId = null;
    this.taskForm.reset({ status: 'Not Started' });
  }

  toggleTasksDisplay(): void {
    this.showAllTasks = !this.showAllTasks;
  }

  /**
   * 🔧 Handles both Firestore Timestamp and native Date types safely.
   */
  convertToDate(input: any): Date {
    return input instanceof Date
      ? input
      : (input && typeof input.toDate === 'function')
        ? input.toDate()
        : new Date(input);
  }
  loadData(): void {
    this.firestoreService.getTasks().pipe(take(1)).subscribe(tasks => {
      this.tasks = tasks.map(task => ({
        ...task,
        dueDate: task.dueDate instanceof Date
          ? task.dueDate
          : (task.dueDate && typeof task.dueDate === 'object' && 'toDate' in task.dueDate
              ? (task.dueDate as any).toDate()
              : new Date(task.dueDate))
      }));
      this.updateAssigneeList();
      this.applyFilters();
    });
  }
}

<div class="tasks-container">
  <h1>Tasks</h1>

  <!-- Add/Edit Task Form (Conditionally Displayed) -->
  <div class="task-form-container" *ngIf="isEditing">
    <h2>{{ editingTaskId === null ? 'Add New Task' : 'Edit Task' }}</h2>
    <form [formGroup]="taskForm" (ngSubmit)="saveTask()">

      <mat-form-field appearance="outline">
        <mat-label>Task Name</mat-label>
        <input matInput formControlName="name" required>
        <mat-error *ngIf="taskForm.get('name')?.hasError('required')">Task name is required</mat-error>
      </mat-form-field>

      <mat-form-field appearance="outline">
        <mat-label>Assignee</mat-label>
        <input matInput formControlName="assignee" required>
         <mat-error *ngIf="taskForm.get('assignee')?.hasError('required')">Assignee is required</mat-error>
      </mat-form-field>

      <mat-form-field appearance="outline">
        <mat-label>Due Date</mat-label>
        <input matInput [matDatepicker]="picker" formControlName="dueDate" required>
        <mat-hint>MM/DD/YYYY</mat-hint>
        <mat-datepicker-toggle matSuffix [for]="picker"></mat-datepicker-toggle>
        <mat-datepicker #picker></mat-datepicker>
         <mat-error *ngIf="taskForm.get('dueDate')?.hasError('required')">Due date is required</mat-error>
      </mat-form-field>

      <mat-form-field appearance="outline">
        <mat-label>Status</mat-label>
        <mat-select formControlName="status" required>
          <mat-option *ngFor="let status of taskStatuses" [value]="status">
            {{ status }}
          </mat-option>
        </mat-select>
         <mat-error *ngIf="taskForm.get('status')?.hasError('required')">Status is required</mat-error>
      </mat-form-field>

      <div class="form-actions">
        <button mat-raised-button color="primary" type="submit" [disabled]="taskForm.invalid">Save Task</button>
        <button mat-stroked-button type="button" (click)="cancelEdit()">Cancel</button>
      </div>
    </form>
  </div>

  <!-- Task List & Filters -->
  <div class="task-list-container" *ngIf="!isEditing">

    <!-- Filter Controls -->
    <div class="filter-controls bg-white">
      <mat-form-field appearance="outline" class="filter-assignee">
        <mat-select [(ngModel)]="selectedAssignee" (ngModelChange)="applyFilters()" placeholder="Choose Assignee" text-blue-500>
          <mat-option [value]="null">All Assignees</mat-option>
          <mat-option *ngFor="let assignee of assigneeList" [value]="assignee">
            {{ assignee }}
          </mat-option>
        </mat-select>
      </mat-form-field>

      <mat-form-field appearance="outline" class="filter-date">
        <input matInput 
               [matDatepicker]="filterDatePicker" 
               [(ngModel)]="selectedDate" 
               (dateChange)="applyFilters()"
               placeholder=" Choose date">
        <mat-datepicker-toggle matIconSuffix [for]="filterDatePicker"></mat-datepicker-toggle>
        <mat-datepicker #filterDatePicker></mat-datepicker>
      </mat-form-field>

       <button mat-flat-button color="primary" class="add-task-button" (click)="showAddTaskForm()">
         <mat-icon>add</mat-icon> Add Task
       </button>
    </div>

    <!-- Task List Display -->
    <div class="task-list">
      <!-- Iterate over filteredTasks with slicing for pagination -->
      <div class="task-card" *ngFor="let task of (showAllTasks ? filteredTasks : filteredTasks.slice(0, 6))">
        <div class="task-info">
          <h3 class="task-name">{{ task.name }}</h3>
          <p class="task-assignee">Assignee: {{ task.assignee }}</p>
          <p class="task-due-date">
            Due: {{ convertToDate(task.dueDate) | date: 'mediumDate' }}
          </p>


        </div>
        <div class="task-status">
          <span class="status-badge" [class]="task.status.toLowerCase().replace(' ', '-')">
            {{ task.status }}
          </span>
        </div>
        <div class="task-actions">
          <button mat-icon-button color="primary" aria-label="Edit task" (click)="editTask(task)">
            <mat-icon>edit</mat-icon>
          </button>
          <button mat-icon-button color="warn" aria-label="Delete task" (click)="deleteTask(task.id!)">
            <mat-icon>delete</mat-icon>
          </button>
        </div>
      </div>

      <!-- Show More/Less Button -->
      <div *ngIf="filteredTasks.length > 6" class="show-more-container">
        <button mat-stroked-button (click)="toggleTasksDisplay()">
          <mat-icon>{{ showAllTasks ? 'expand_less' : 'expand_more' }}</mat-icon>
          {{ showAllTasks ? 'Show Less' : 'Show More' }}
        </button>
      </div>

      <!-- Update empty message check -->
      <div *ngIf="filteredTasks.length === 0" class="no-tasks">
        No tasks found matching the current filters. Try adjusting filters or adding a task!
      </div>
    </div>
  </div>
</div>
<div *ngIf="tasks.length === 0" class="empty-state">
  <img src="assets/empty-tasks.svg" alt="Empty" class="empty-icon" />
  <p>No tasks available. Upload a file or add a task.</p>
</div>

