import { Component, OnInit } from '@angular/core';

interface TeamMember {
  name: string;
  role: string;
  emails: string[];
  avatarColor: string;
}

@Component({
  selector: 'app-teams',
  templateUrl: './teams.component.html',
  styleUrls: ['./teams.component.scss']
})
export class TeamsComponent implements OnInit {
  teamMembers: TeamMember[] = [
    {
      name: 'Alice Smith',
      role: 'Designer',
      emails: ['alice@smith', 'alice@emil.com'],
      avatarColor: '#4361ee'
    },
    {
      name: 'Bob Johnson',
      role: 'Developer',
      emails: ['bob.johnson.com', 'bob.johnson.com'],
      avatarColor: '#7209b7'
    },
    {
      name: 'Carol Williams',
      role: 'Manager',
      emails: ['carol@amail.com', 'carol@amair.com'],
      avatarColor: '#4cc9f0'
    },
    {
      name: 'David Brown',
      role: 'Developer',
      emails: ['david@amail.com', 'davidbrown.com'],
      avatarColor: '#ef4444'
    }
  ];

  constructor() {}

  ngOnInit() {
    console.log('Teams component initialized');
  }

  deleteTeamMember(index: number) {
    this.teamMembers.splice(index, 1);
  }
}
