import { Component } from '@angular/core';
import { AuthService } from '../services/auth.service';
import { Router } from '@angular/router';
import { MatIconModule } from '@angular/material/icon'; // âœ… Add this
import { MatTooltipModule } from '@angular/material/tooltip'; // Optional if using matTooltip
import { MatButtonModule } from '@angular/material/button'; // Optional if using buttons
import { RouterModule } from '@angular/router'; // Optional if using routerLink
import { CommonModule } from '@angular/common';


@Component({
  selector: 'app-header',
  standalone: true,
  imports: [
    CommonModule,
    MatIconModule,      // âœ… Required for <mat-icon>
    MatTooltipModule,   // ðŸ§  Optional, only if using tooltips
    MatButtonModule,    // ðŸ§  Optional, if using buttons
    RouterModule        // Optional, if you're routing from header
  ],
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {
  isLoggedIn = false;

  constructor(private auth: AuthService, private router: Router) {
    this.auth.getUser().subscribe(user => {
      this.isLoggedIn = !!user;
    });
  }

  logout() {
    this.auth.logout().then(() => {
      this.router.navigate(['/auth']);
    });
  }

  goToLogin() {
    this.router.navigate(['/auth']);
  }
}
