import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {
  // Data for Task Status (with dueDate included)
  taskStatus = [
    { name: 'Task 1', status: 'Completed', dueDate: new Date('2025-01-10') },
    { name: 'Task 2', status: 'In Progress', dueDate: new Date('2025-04-12') },
    { name: 'Task 3', status: 'Completed', dueDate: new Date('2025-03-15') },
    { name: 'Task 4', status: 'Completed', dueDate: new Date('2025-02-11') },
    { name: 'Task 5', status: 'Completed', dueDate: new Date('2025-01-10') },
    // Add the dueDate for the rest of the tasks
  ];

  // Dynamic Task Summary Variables
  totalTasks: number = 0;
  tasksCompleted: number = 0;
  overdueTasks: number = 0;

  constructor() {}

  ngOnInit(): void {
    this.calculateTaskSummary();
  }

  calculateTaskSummary() {
    // Calculate total tasks
    this.totalTasks = this.taskStatus.length;

    // Calculate completed tasks
    this.tasksCompleted = this.taskStatus.filter(task => task.status === 'Completed').length;

    // Calculate overdue tasks
    const currentDate = new Date();
    this.overdueTasks = this.taskStatus.filter(task => task.dueDate < currentDate && task.status !== 'Completed').length;

    console.log('Total Tasks:', this.totalTasks);
    console.log('Completed Tasks:', this.tasksCompleted);
    console.log('Overdue Tasks:', this.overdueTasks);
  }
}
<div class="dashboard-container">
  <!-- Project Overview -->
  <mat-card class="dashboard-card">
    <mat-card-header>
      <mat-card-title>Project Overview</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <div class="progress-bar">
        <mat-progress-bar mode="determinate" [value]="progress"></mat-progress-bar>
      </div>
      <p>{{ progress | number: '1.2-2' }}% Completed</p>
    </mat-card-content>
  </mat-card>

  <!-- Task Status Table -->
  <mat-card class="task-status-card">
    <mat-card-header>
      <mat-card-title>Task Status</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <!-- Task Status Filter Dropdown -->
      <mat-form-field appearance="fill">
        <mat-label>Filter by Status</mat-label>
        <mat-select [(value)]="selectedStatus" (selectionChange)="filterTasks(selectedStatus)">
          <mat-option value="All">All Tasks</mat-option>
          <mat-option value="In Progress">In Progress</mat-option>
          <mat-option value="Completed">Completed</mat-option>
          <mat-option value="Overdue">Overdue</mat-option>
        </mat-select>
      </mat-form-field>

      <table mat-table [dataSource]="filteredTasks" class="mat-elevation-z8">
        <ng-container matColumnDef="task">
          <th mat-header-cell *matHeaderCellDef> Task </th>
          <td mat-cell *matCellDef="let task"> {{task.name}} </td>
        </ng-container>

        <ng-container matColumnDef="status">
          <th mat-header-cell *matHeaderCellDef> Status </th>
          <td mat-cell *matCellDef="let task"> {{task.status}} </td>
        </ng-container>

        <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
      </table>
    </mat-card-content>
  </mat-card>

  <!-- Task Summary -->
  <mat-card class="task-summary-card">
    <mat-card-header>
      <mat-card-title>Task Summary</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <div class="task-summary">
        <div class="task-summary-box">Total Tasks: {{ totalTasks }}</div>
        <div class="task-summary-box">Tasks Completed: {{ tasksCompleted }}</div>
        <div class="task-summary-box">Overdue Tasks: {{ overdueTasks }}</div>
      </div>
    </mat-card-content>
  </mat-card>

  <!-- Team Performance -->
  <mat-card class="team-performance-card">
    <mat-card-header>
      <mat-card-title>Team Performance</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <!-- Team Member Filter Dropdown -->
      <mat-form-field appearance="fill">
        <mat-label>Filter by Team Member</mat-label>
        <mat-select [(value)]="selectedTeamMember" (selectionChange)="filterTeamMember(selectedTeamMember)">
          <mat-option value="All">All Members</mat-option>
          <mat-option value="John">John</mat-option>
          <mat-option value="Alice">Alice</mat-option>
          <mat-option value="Bob">Bob</mat-option>
        </mat-select>
      </mat-form-field>

      <ol class="list">
        <li *ngFor="let member of filteredTeamData">
          {{ member.member }}: {{ member.tasksAssigned }} tasks assigned, {{ member.tasksCompleted }} tasks completed
        </li>
      </ol>
    </mat-card-content>
  </mat-card>

  <!-- Upcoming Deadlines -->
  <mat-card class="upcoming-deadlines-card">
    <mat-card-header>
      <mat-card-title>Upcoming Deadlines</mat-card-title>
    </mat-card-header>
    <mat-card-content>
      <!-- Deadline Filter Dropdown -->
      <mat-form-field appearance="fill">
        <mat-label>Filter by Deadline</mat-label>
        <mat-select [(value)]="selectedDeadline" (selectionChange)="filterDeadlines(selectedDeadline)">
          <mat-option value="All">All Dates</mat-option>
          <mat-option value="2025-04-10">April 10</mat-option>
          <mat-option value="2025-04-12">April 12</mat-option>
          <mat-option value="2025-04-15">April 15</mat-option>
        </mat-select>
      </mat-form-field>

      <table mat-table [dataSource]="filteredDeadlines" class="mat-elevation-z8">
        <ng-container matColumnDef="task">
          <th mat-header-cell *matHeaderCellDef> Task </th>
          <td mat-cell *matCellDef="let task"> {{task.name}} </td>
        </ng-container>

        <ng-container matColumnDef="deadline">
          <th mat-header-cell *matHeaderCellDef> Deadline </th>
          <td mat-cell *matCellDef="let task"> {{task.deadline}} </td>
        </ng-container>

        <tr mat-header-row *matHeaderRowDef="displayedColumnsDeadlines"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumnsDeadlines;"></tr>
      </table>
    </mat-card-content>
  </mat-card>
</div>
