import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {
  // Data for Task Summary
  totalTasks: number = 45;
  tasksCompleted: number = 20;
  overdueTasks: number = 5;

  // Task Completion Percentage
  progress: number = (this.tasksCompleted / this.totalTasks) * 100;

  // Task Data for different statuses (All Tasks, In Progress, Completed)
  allTasks = [
    { id: 1, name: 'Task 1', status: 'Completed' },
    { id: 2, name: 'Task 2', status: 'In Progress' },
    { id: 3, name: 'Task 3', status: 'Completed' },
    { id: 4, name: 'Task 4', status: 'Overdue' },
    { id: 5, name: 'Task 5', status: 'In Progress' },
    { id: 6, name: 'Task 6', status: 'Completed' }
  ];

  // Filtered Tasks (default to All Tasks)
  filteredTasks = [...this.allTasks];

  // Columns for the table
  displayedColumns: string[] = ['task', 'status'];

  constructor() {}

  ngOnInit(): void {}

  // Function to filter tasks based on status
  filterTasks(status: string) {
    if (status === 'All') {
      this.filteredTasks = [...this.allTasks];
    } else {
      this.filteredTasks = this.allTasks.filter(task => task.status === status);
    }
  }
}
