    const canvas = this.donutChartRef.nativeElement;
    const ctx = canvas.getContext('2d');

    if (this.donutChart) {
      this.donutChart.destroy();
    }

    this.donutChart = new Chart(ctx, {
      type: 'doughnut',
      data: {
        labels: this.donutChartLabels,
        datasets: [{
          data: this.donutChartData,
          backgroundColor: [
            '#4ade80',
            '#60a5fa',
            '#a78bfa'
          ],
          borderWidth: 0
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        cutout: '75%',
        plugins: {
          legend: { display: false },
          tooltip: { enabled: true }
        }
      }
    });
  }

  private initializePerformanceChart() {
    if (!this.performanceChartRef) return;
    const canvas = this.performanceChartRef.nativeElement;
    const ctx = canvas.getContext('2d');

    if (this.performanceChart) {
      this.performanceChart.destroy();
    }

    this.performanceChart = new Chart(ctx, {
      type: 'line',
      data: {
        labels: this.performanceChartLabels,
        datasets: [{
          label: 'Tasks Completed Weekly',
          data: this.performanceChartData,
          fill: false,
          borderColor: '#60a5fa',
          pointBackgroundColor: '#60a5fa',
          pointBorderColor: '#60a5fa',
          pointRadius: 4,
          tension: 0.3
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
          y: {
            beginAtZero: true,
            ticks: { color: '#a0aec0' },
            grid: { color: 'rgba(255, 255, 255, 0.1)' }
          },
          x: {
            ticks: { color: '#a0aec0' },
            grid: { display: false }
          }
        },
        plugins: {
          legend: { display: false }
        }
      }
    });
  }

  toggleProjects(): void {
    this.showAllProjects = !this.showAllProjects;
  }
}
