import { Component, OnInit, CUSTOM_ELEMENTS_SCHEMA } from '@angular/core';
import { CommonModule, DatePipe } from '@angular/common';
import { ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { FormsModule } from '@angular/forms';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatChipsModule } from '@angular/material/chips';
import { FirestoreService } from '../services/firestore.service';
import { TeamMember } from '../models/team.model';
import { take } from 'rxjs';
import { RefreshService } from 'src/app/services/refresh.service';

@Component({
  selector: 'app-teams',
  templateUrl: './teams.component.html',
  styleUrls: ['./teams.component.scss'],
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    FormsModule,
    MatButtonModule,
    MatIconModule,
    MatFormFieldModule,
    MatInputModule,
    MatSelectModule,
    MatChipsModule,

  ],
  providers: [DatePipe],
  schemas: [CUSTOM_ELEMENTS_SCHEMA]
})
export class TeamsComponent implements OnInit {
  teamMembers: TeamMember[] = [];
  filteredTeamMembers: TeamMember[] = [];
  teamMemberForm!: FormGroup;
  isEditing = false;
  editingMemberId: string | null = null;
  nextId = 1;
  roleList: string[] = [];
  filterName: string = '';
  selectedRole: string = 'All';
  showAllMembers = false;

  constructor(
    private fb: FormBuilder,
    private datePipe: DatePipe,
    private firestoreService: FirestoreService,
    private refreshService: RefreshService
  ) {}

  ngOnInit(): void {
    this.teamMemberForm = this.fb.group({
      name: ['', Validators.required],
      role: ['', Validators.required],
      email: [''],
      avatarColor: [this.getRandomColor()]
    });

    this.loadData();

    this.refreshService.refresh$.subscribe(() => {
      this.loadData();
    });
  }

  loadData(): void {
    this.firestoreService.getTeamMembers().pipe(take(1)).subscribe(members => {
      this.teamMembers = members;
      this.updateRoleList();
      this.applyFilters();
    });
  }

  updateRoleList(): void {
    const roles = new Set(this.teamMembers.map(member => member.role));
    this.roleList = ['All', ...Array.from(roles).sort()];
  }

  applyFilters(): void {
    let tempMembers = [...this.teamMembers];
    if (this.filterName) {
      const filterLower = this.filterName.toLowerCase();
      tempMembers = tempMembers.filter(member => member.name.toLowerCase().includes(filterLower));
    }
    if (this.selectedRole && this.selectedRole !== 'All') {
      tempMembers = tempMembers.filter(member => member.role === this.selectedRole);
    }
    this.filteredTeamMembers = tempMembers;
  }

  clearNameFilter(): void {
    this.filterName = '';
    this.applyFilters();
  }

  showAddMemberForm(): void {
    this.isEditing = true;
    this.editingMemberId = null;
    this.teamMemberForm.reset({ avatarColor: this.getRandomColor() });
  }

  editMember(member: TeamMember): void {
    this.isEditing = true;
    this.editingMemberId = member.id || null;
    this.teamMemberForm.patchValue({
      name: member.name,
      role: member.role,
      email: member.emails.length > 0 ? member.emails[0] : '',
      avatarColor: member.avatarColor
    });
  }

  saveMember(): void {
    if (this.teamMemberForm.invalid) return;

    const formValue = this.teamMemberForm.value;
    const emails = formValue.email ? [formValue.email] : [];
    const newMember: TeamMember = {
      name: formValue.name,
      role: formValue.role,
      emails,
      avatarColor: formValue.avatarColor
    };

    if (this.editingMemberId) {
      this.firestoreService.updateTeamMember(this.editingMemberId, newMember).pipe(take(1)).subscribe(() => {
        const index = this.teamMembers.findIndex(m => m.id === this.editingMemberId);
        if (index > -1) this.teamMembers[index] = { ...this.teamMembers[index], ...newMember };
        this.afterChange();
      });
    } else {
      this.firestoreService.addTeamMember(newMember).pipe(take(1)).subscribe(docRef => {
        this.teamMembers.push({ id: docRef.id, ...newMember });
        this.afterChange();
      });
    }
  }

  deleteTeamMember(memberId: string): void {
    this.firestoreService.deleteTeamMember(memberId).pipe(take(1)).subscribe(() => {
      this.teamMembers = this.teamMembers.filter(m => m.id !== memberId);
      this.afterChange();
    });
  }

  afterChange(): void {
    this.updateRoleList();
    this.applyFilters();
    this.cancelEdit();
  }

  cancelEdit(): void {
    this.isEditing = false;
    this.editingMemberId = null;
    this.teamMemberForm.reset({ avatarColor: this.getRandomColor() });
  }

  getRandomColor(): string {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  }

  toggleMembersDisplay(): void {
    this.showAllMembers = !this.showAllMembers;
  }
}

  <h1>Team Members</h1>

  <!-- Add/Edit Team Member Form (Conditionally Displayed) -->
  <div class="team-form-container" *ngIf="isEditing">
    <h2>{{ editingMemberId === null ? 'Add New Member' : 'Edit Member' }}</h2>
    <form [formGroup]="teamMemberForm" (ngSubmit)="saveMember()">

      <mat-form-field appearance="outline">
        <mat-label>Member Name</mat-label>
        <input matInput formControlName="name" required>
        <mat-error *ngIf="teamMemberForm.get('name')?.hasError('required')">Name is required</mat-error>
      </mat-form-field>

      <mat-form-field appearance="outline">
        <mat-label>Role</mat-label>
        <input matInput formControlName="role" required>
        <mat-error *ngIf="teamMemberForm.get('role')?.hasError('required')">Role is required</mat-error>
      </mat-form-field>

      <mat-form-field appearance="outline">
        <mat-label>Email</mat-label>
        <input matInput formControlName="email" type="email">
        <mat-error *ngIf="teamMemberForm.get('email')?.hasError('email')">Please enter a valid email address</mat-error>
      </mat-form-field>

      <div class="form-actions">
        <button mat-raised-button color="primary" type="submit" [disabled]="teamMemberForm.invalid">Save Member</button>
        <button mat-stroked-button type="button" (click)="cancelEdit()">Cancel</button>
      </div>
    </form>
  </div>

  <!-- Team List & Filters -->
  <div class="team-list-container" *ngIf="!isEditing">

    <!-- Filter Controls -->
    <div class="filter-controls">
       <mat-form-field appearance="outline" class="filter-name">
        <input matInput 
               [(ngModel)]="filterName" 
               (ngModelChange)="applyFilters()" 
               placeholder="Enter name...">
         <button mat-icon-button matSuffix *ngIf="filterName" (click)="clearNameFilter()" aria-label="Clear name filter">
            <mat-icon>close</mat-icon>
        </button>
      </mat-form-field>

      <mat-form-field appearance="outline" class="filter-role">
        <mat-select [(ngModel)]="selectedRole" (ngModelChange)="applyFilters()">
          <mat-option *ngFor="let role of roleList" [value]="role">
            {{ role }}
          </mat-option>
        </mat-select>
      </mat-form-field>

      <button mat-flat-button color="primary" class="add-member-button" (click)="showAddMemberForm()">
        <mat-icon>add</mat-icon> Add Member
      </button>
    </div>

    <!-- Team Grid Display -->
    <div class="teams-grid">
      <!-- Iterate over filteredTeamMembers with slicing for pagination -->
      <div class="team-card" *ngFor="let member of (showAllMembers ? filteredTeamMembers : filteredTeamMembers.slice(0, 6))">
        <div class="avatar" [style.background-color]="member.avatarColor">
          {{ member.name.charAt(0) }}
        </div>
        <div class="member-info">
          <h3 class="member-name">{{ member.name }}</h3>
          <p class="member-role">{{ member.role }}</p>
          <mat-chip-list aria-label="Member emails">
            <mat-chip *ngFor="let email of member.emails">{{ email }}</mat-chip>
          </mat-chip-list>
        </div>
        <div class="member-actions">
          <button mat-icon-button color="primary" aria-label="Edit member" (click)="editMember(member)">
            <mat-icon>edit</mat-icon>
          </button>
          <button mat-icon-button color="warn" aria-label="Delete member" (click)="deleteTeamMember(member.id!)">
            <mat-icon>delete</mat-icon>
          </button>
        </div>
      </div>
    </div>

    <!-- Show More/Less Button -->
    <div *ngIf="filteredTeamMembers.length > 6" class="show-more-container">
      <button mat-stroked-button (click)="toggleMembersDisplay()">
        <mat-icon>{{ showAllMembers ? 'expand_less' : 'expand_more' }}</mat-icon>
        {{ showAllMembers ? 'Show Less' : 'Show More' }}
      </button>
    </div>

    <!-- Message when no members match filter -->
    <div *ngIf="filteredTeamMembers.length === 0" class="no-members">
       No team members found matching the current filters. Try clearing filters or adding a member!
    </div>

  </div>


<div *ngIf="teamMembers.length === 0" class="empty-state">
  <img src="assets/empty-team.svg" alt="No team" class="empty-icon" />
  <p>No team members found. Upload or add a team member.</p>
</div>

