  constructor() {}

  ngOnInit(): void {
    this.calculateTaskSummary();
    this.initializeFilters();
  }

  initializeFilters(): void {
    // Initialize all filters with their default values
    this.filterTasks('All');
    this.filterTeamMember('All');
    this.filterDeadlines('All');
  }

  calculateTaskSummary() {
    this.totalTasks = this.taskStatus.length;
    this.tasksCompleted = this.taskStatus.filter(task => task.status === 'Completed').length;
    const currentDate = new Date();
    this.overdueTasks = this.taskStatus.filter(task => 
      new Date(task.dueDate) < currentDate && task.status !== 'Completed'
    ).length;
    this.progress = (this.tasksCompleted / this.totalTasks) * 100;
  }

  // Filter functions
  filterTasks(status: string): void {
    this.selectedStatus = status;
    if (status === 'All') {
      this.filteredTasks = [...this.taskStatus];
    } else {
      this.filteredTasks = this.taskStatus.filter(task => task.status === status);
    }
    this.showMoreTaskStatus = this.filteredTasks.length > this.taskStatusLimit;
  }

  filterTeamMember(member: string): void {
    this.selectedTeamMember = member;
    if (member === 'All') {
      this.filteredTeamData = [...this.teamData];
    } else {
      this.filteredTeamData = this.teamData.filter(data => data.member === member);
    }
    this.showMoreTeamPerformance = this.filteredTeamData.length > this.teamPerformanceLimit;
  }

  filterDeadlines(date: string): void {
    this.selectedDeadline = date;
    if (date === 'All') {
      this.filteredDeadlines = [...this.upcomingDeadlines];
    } else {
      this.filteredDeadlines = this.upcomingDeadlines.filter(task => task.deadline === date);
    }
    this.showMoreUpcomingDeadlines = this.filteredDeadlines.length > this.upcomingDeadlinesLimit;
  }

  // Load More/Less functions
  loadMoreTaskStatus() {
    this.taskStatusLimit += 12;
    this.showMoreTaskStatus = this.taskStatusLimit < this.filteredTasks.length;
  }

  loadMoreTeamPerformance() {
    this.teamPerformanceLimit += 6;
    this.showMoreTeamPerformance = this.teamPerformanceLimit < this.filteredTeamData.length;
  }

  loadMoreUpcomingDeadlines() {
    this.upcomingDeadlinesLimit += 6;
    this.showMoreUpcomingDeadlines = this.upcomingDeadlinesLimit < this.filteredDeadlines.length;
  }

  showLessTaskStatus() {
    this.taskStatusLimit = 12;
    this.showMoreTaskStatus = true;
  }

  showLessTeamPerformance() {
    this.teamPerformanceLimit = 6;
    this.showMoreTeamPerformance = true;
  }

  showLessUpcomingDeadlines() {
    this.upcomingDeadlinesLimit = 6;
    this.showMoreUpcomingDeadlines = true;
  }
