import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatToolbarModule } from '@angular/material/toolbar';
import { MatIconModule } from '@angular/material/icon';
import { MatButtonModule } from '@angular/material/button';
import { MatMenuModule } from '@angular/material/menu';
import { MatDialog, MatDialogModule } from '@angular/material/dialog';
import { RouterModule, Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';
import { FirestoreService } from 'src/app/services/firestore.service';
import { User } from '@angular/fire/auth';
import { Observable } from 'rxjs';
import { ChangePasswordDialogComponent } from 'src/app/change-password-dialog/change-password-dialog.component';
import { MatDividerModule } from '@angular/material/divider';
import * as XLSX from 'xlsx';
import { Project } from 'src/app/models/project.model';
import { Task } from 'src/app/models/task.model';
import { TeamMember } from 'src/app/models/team.model';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
  standalone: true,
  imports: [
    CommonModule,
    MatToolbarModule,
    MatIconModule,
    MatButtonModule,
    MatMenuModule,
    MatDialogModule,
    RouterModule,
    MatDividerModule
  ]
})
export class HeaderComponent {
  user$: Observable<User | null>;
  private dialog = inject(MatDialog);
  fileMessage = '';

  constructor(
    public authService: AuthService,
    private firestoreService: FirestoreService,
    private router: Router
  ) {
    this.user$ = authService.getUser();
  }

  logout() {
    this.authService.logout().then(() => this.router.navigate(['/auth']));
  }

  openChangePasswordDialog() {
    this.dialog.open(ChangePasswordDialogComponent, {
      width: '600px',
      maxWidth: '95vw',
      panelClass: 'custom-dialog-container',
      disableClose: false,
      autoFocus: true
    });
  }

  onFileUpload(event: any) {
    const file = event.target.files[0];
    if (!file) return;

    const allowedTypes = ['application/vnd.openxmlformats-officedocument.spreadsheetml.sheet', 'application/vnd.ms-excel'];
    if (!allowedTypes.includes(file.type)) {
      this.fileMessage = '❌ File type not supported. Please upload Excel file.';
      return;
    }

    const reader = new FileReader();
    reader.onload = (e: any) => {
      const data = new Uint8Array(e.target.result);
      const workbook = XLSX.read(data, { type: 'array' });

      workbook.SheetNames.forEach(sheetName => {
        const json = XLSX.utils.sheet_to_json(workbook.Sheets[sheetName]);

        if (sheetName.toLowerCase().includes('project')) {
          json.forEach((item: any) => {
            const project: Project = {
              name: item.name,
              description: item.description || '',
              dueDate: new Date(item.dueDate),
              status: item.status || 'Not Started'
            };
            this.firestoreService.addProject(project).subscribe();
          });
        }

        if (sheetName.toLowerCase().includes('task')) {
          json.forEach((item: any) => {
            const task: Task = {
              name: item.name,
              assignee: item.assignee,
              dueDate: new Date(item.dueDate),
              status: item.status || 'Not Started'
            };
            this.firestoreService.addTask(task).subscribe();
          });
        }

        if (sheetName.toLowerCase().includes('team')) {
          json.forEach((item: any) => {
            const member: TeamMember = {
              name: item.name,
              role: item.role,
              emails: item.email ? [item.email] : [],
              avatarColor: this.getRandomColor()
            };
            this.firestoreService.addTeamMember(member).subscribe();
          });
        }
      });

      this.fileMessage = '✅ File uploaded and processed successfully.';
    };

    reader.onerror = () => {
      this.fileMessage = '❌ Error uploading file. Please try again.';
    };

    reader.readAsArrayBuffer(file);
  }

  private getRandomColor(): string {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  }
}
