import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatToolbarModule } from '@angular/material/toolbar';
import { MatIconModule } from '@angular/material/icon';
import { MatButtonModule } from '@angular/material/button';
import { MatMenuModule } from '@angular/material/menu';
import { MatDialog, MatDialogModule } from '@angular/material/dialog';
import { RouterModule, Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';
import { FirestoreService } from 'src/app/services/firestore.service';
import { User } from '@angular/fire/auth';
import { Observable } from 'rxjs';
import { ChangePasswordDialogComponent } from 'src/app/change-password-dialog/change-password-dialog.component';
import { MatDividerModule } from '@angular/material/divider';
import * as XLSX from 'xlsx';
import { Project } from 'src/app/models/project.model';
import { Task } from 'src/app/models/task.model';
import { TeamMember } from 'src/app/models/team.model';
import { MatSnackBar } from '@angular/material/snack-bar';
import { MatProgressSpinnerModule } from '@angular/material/progress-spinner';


@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
  standalone: true,
  imports: [
    CommonModule,
    MatToolbarModule,
    MatIconModule,
    MatButtonModule,
    MatMenuModule,
    MatDialogModule,
    RouterModule,
    MatDividerModule
  ]
})
export class HeaderComponent {
  user$: Observable<User | null>;
  isUploading = false;
  fileMessage = '';
  private dialog = inject(MatDialog);

  constructor(
    public authService: AuthService,
    private firestoreService: FirestoreService,
    private router: Router,
    private snackBar: MatSnackBar
  ) {
    this.user$ = authService.getUser();
  }

  logout() {
    this.authService.logout().then(() => this.router.navigate(['/auth']));
  }

  openChangePasswordDialog() {
    this.dialog.open(ChangePasswordDialogComponent, {
      width: '600px',
      maxWidth: '95vw',
      panelClass: 'custom-dialog-container',
      disableClose: false,
      autoFocus: true
    });
  }

  onFileUpload(event: any) {
    this.isUploading = true;
    const file = event.target.files[0];
    if (!file) return;

    const allowedTypes = [
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
      'application/vnd.ms-excel',
      'text/csv',
      'application/csv'
    ];
    if (!allowedTypes.includes(file.type)) {
      this.showError('❌ File type not supported. Please upload Excel or CSV.');
      this.isUploading = false;
      return;
    }

    const reader = new FileReader();
    reader.onload = async (e: any) => {
      const data = new Uint8Array(e.target.result);
      let workbook;

      try {
        if (file.type === 'text/csv' || file.type === 'application/csv') {
          const text = new TextDecoder('utf-8').decode(data);
          workbook = XLSX.read(text, { type: 'string' });
        } else {
          workbook = XLSX.read(data, { type: 'array' });
        }

        const [existingProjects, existingTasks, existingTeam] = await Promise.all([
          this.firestoreService.getProjects().toPromise(),
          this.firestoreService.getTasks().toPromise(),
          this.firestoreService.getTeamMembers().toPromise()
        ]);

        workbook.SheetNames.forEach(sheetName => {
          const json = XLSX.utils.sheet_to_json(workbook.Sheets[sheetName]);

          if (sheetName.toLowerCase().includes('project')) {
            json.forEach((item: any) => {
              if (!item.name || !item.dueDate) {
                this.showError('❌ Skipped project with missing required fields');
                return;
              }

              const project: Project = {
                name: item.name.trim(),
                description: item.description || '',
                dueDate: new Date(item.dueDate),
                status: item.status || 'Not Started'
              };

              const isDuplicate = existingProjects.some(p =>
                p.name === project.name &&
                new Date(p.dueDate).getTime() === new Date(project.dueDate).getTime()
              );

              if (!isDuplicate) {
                this.firestoreService.addProject(project).subscribe(() => {
                  this.showSuccess(`✅ Project "${project.name}" added`);
                });
              } else {
                this.showError(`❌ Duplicate project: ${project.name}`);
              }
            });
          }

          if (sheetName.toLowerCase().includes('task')) {
            json.forEach((item: any) => {
              if (!item.name || !item.dueDate || !item.assignee) {
                this.showError('❌ Skipped task with missing required fields');
                return;
              }

              const task: Task = {
                name: item.name.trim(),
                assignee: item.assignee.trim(),
                dueDate: new Date(item.dueDate),
                status: item.status || 'Not Started'
              };

              const isDuplicate = existingTasks.some(t =>
                t.name === task.name &&
                t.assignee === task.assignee &&
                new Date(t.dueDate).getTime() === new Date(task.dueDate).getTime()
              );

              if (!isDuplicate) {
                this.firestoreService.addTask(task).subscribe(() => {
                  this.showSuccess(`✅ Task "${task.name}" added`);
                });
              } else {
                this.showError(`❌ Duplicate task: ${task.name}`);
              }
            });
          }

          if (sheetName.toLowerCase().includes('team')) {
            json.forEach((item: any) => {
              if (!item.name || !item.role) {
                this.showError('❌ Skipped team member with missing required fields');
                return;
              }

              const member: TeamMember = {
                name: item.name.trim(),
                role: item.role.trim(),
                emails: item.email ? [item.email.trim()] : [],
                avatarColor: this.getRandomColor()
              };

              const isDuplicate = existingTeam.some(m =>
                m.name === member.name &&
                m.role === member.role ||
                (member.emails.length && m.emails.includes(member.emails[0]))
              );

              if (!isDuplicate) {
                this.firestoreService.addTeamMember(member).subscribe(() => {
                  this.showSuccess(`✅ Team Member "${member.name}" added`);
                });
              } else {
                this.showError(`❌ Duplicate team member: ${member.name}`);
              }
            });
          }
        });

        this.snackBar.open('✅ File uploaded and processed successfully.', 'Close', {
          duration: 4000,
          panelClass: 'snackbar-success'
        });
      } catch (error) {
        console.error(error);
        this.showError('❌ Failed to read or process the file.');
      }

      this.isUploading = false;
    };

    reader.onerror = () => {
      this.showError('❌ Error uploading file. Please try again.');
      this.isUploading = false;
    };

    reader.readAsArrayBuffer(file);
  }

  private getRandomColor(): string {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  }

  showSuccess(message: string) {
    this.snackBar.open(message, 'Close', {
      duration: 4000,
      panelClass: 'snackbar-success'
    });
  }

  showError(message: string) {
    this.snackBar.open(message, 'Close', {
      duration: 4000,
      panelClass: 'snackbar-error'
    });
  }
}
<div class="header-toolbar">
  <span class="spacer"></span>

  <!-- 📂 File Upload Button -->
  <label for="file-upload" class="upload-icon">
    <input
      id="file-upload"
      type="file"
      accept=".xlsx, .xls, .csv"
      hidden
      (change)="onFileUpload($event)"
    />
    <mat-icon matTooltip="Upload Excel/CSV">upload_file</mat-icon>
  </label>

  <!-- 🟢/🔴 Upload Status Message -->
  <div
    *ngIf="fileMessage"
    [ngClass]="{
      'success-msg': fileMessage.includes('✅'),
      'error-msg': fileMessage.includes('❌')
    }"
  >
    {{ fileMessage }}
  </div>

  <!-- ⌛ Progress Spinner -->
  <mat-progress-spinner
    *ngIf="isUploading"
    diameter="24"
    mode="indeterminate"
    color="accent"
    class="upload-spinner"
  ></mat-progress-spinner>

  <div class="header-actions">
    <!-- 👤 User Icon or Login -->
    <ng-container *ngIf="user$ | async as user; else loginBtn">
      <button mat-icon-button [matMenuTriggerFor]="userMenu" matTooltip="User Menu">
        <mat-icon>account_circle</mat-icon>
      </button>
      <mat-menu #userMenu="matMenu">
        <button mat-menu-item class="user-info-item">
          <mat-icon class="user-icon">mail</mat-icon>
          <span><strong>Email:</strong> {{ user.email }}</span>
        </button>
        <button mat-menu-item (click)="openChangePasswordDialog()">
          <mat-icon class="user-icon">lock</mat-icon>
          <span>Change Password</span>
        </button>
        <button mat-menu-item (click)="logout()">
          <mat-icon class="user-icon">logout</mat-icon>
          <span>Logout</span>
        </button>
      </mat-menu>
    </ng-container>

    <!-- 🔐 Login Button -->
    <ng-template #loginBtn>
      <button mat-icon-button [routerLink]="['/auth']" matTooltip="Login">
        <mat-icon>login</mat-icon>
      </button>
    </ng-template>
  </div>
</div>
