import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { MatButtonModule } from '@angular/material/button';

@Component({
  selector: 'app-welcome',
  templateUrl: './welcome.component.html',
  styleUrls: ['./welcome.component.scss'],
  standalone: true,
  imports: [CommonModule, MatButtonModule]
})
export class WelcomeComponent {

  constructor(private router: Router) {}

  goToDashboard(): void {
    this.router.navigate(['/dashboard']);
  }
  launchRocket(event: Event) {
    const button = (event.currentTarget as HTMLElement);
    const rocket = button.querySelector('.rocket');

    if (rocket) {
      rocket.classList.add('fly-away');

      // Delay navigation to let the rocket finish flying
      setTimeout(() => {
        this.goToDashboard(); // Your existing navigation method
      }, 1200); // Match with animation duration
    }
  }

}
