import { Component, OnInit } from '@angular/core';
import { CommonModule, DatePipe } from '@angular/common';
import { ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { FormsModule } from '@angular/forms';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatDatepickerModule } from '@angular/material/datepicker';
import { MatNativeDateModule } from '@angular/material/core';
import { MatSelectModule } from '@angular/material/select';
import { ThemeToggleComponent } from '../shared/theme-toggle/theme-toggle.component';
import { FirestoreService } from '../services/firestore.service';
import { Project } from '../models/project.model';
import { take } from 'rxjs';
import { RefreshService } from 'src/app/services/refresh.service';

@Component({
  selector: 'app-projects',
  templateUrl: './projects.component.html',
  styleUrls: ['./projects.component.scss'],
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    FormsModule,
    MatButtonModule,
    MatIconModule,
    MatFormFieldModule,
    MatInputModule,
    MatDatepickerModule,
    MatNativeDateModule,
    MatSelectModule,
    ThemeToggleComponent
  ],
  providers: [DatePipe]
})
export class ProjectsComponent implements OnInit {
  projects: Project[] = [];
  filteredProjects: Project[] = [];
  projectForm!: FormGroup;
  isEditing = false;
  editingProjectId: string | null = null;
  projectStatuses: string[] = ['Not Started', 'In Progress', 'Completed'];
  selectedDate: Date | null = null;
  showAllProjects = false;

  // ðŸ” Filtering states
  filterProjectName: string = '';
  filterAssignee: string = '';
  filterStatus: string = '';
  filterDate: Date | null = null;
  assigneeList: string[] = [];

  constructor(
    private fb: FormBuilder,
    private datePipe: DatePipe,
    private firestoreService: FirestoreService,
    private refreshService: RefreshService
  ) {}

  ngOnInit(): void {
    this.projectForm = this.fb.group({
      name: ['', Validators.required],
      assignee: [''],
      dueDate: [null, Validators.required],
      status: ['Not Started', Validators.required]
    });

    this.loadData();

    // âœ… This ensures new data shows after upload
    this.refreshService.refresh$.subscribe(() => {
      this.loadData();
    });
  }



  applyFilters(): void {
    let temp = [...this.projects];

    if (this.filterProjectName) {
      temp = temp.filter(p => p.name.toLowerCase().includes(this.filterProjectName.toLowerCase()));
    }

    if (this.filterAssignee) {
      temp = temp.filter(p => p.assignee === this.filterAssignee);
    }

    if (this.filterStatus) {
      temp = temp.filter(p => p.status === this.filterStatus);
    }

    if (this.filterDate) {
      const filterTime = new Date(this.filterDate).setHours(0, 0, 0, 0);
      temp = temp.filter(p => {
        const due = new Date(p.dueDate).setHours(0, 0, 0, 0);
        return due === filterTime;
      });
    }

    this.filteredProjects = temp.sort((a, b) => new Date(a.dueDate).getTime() - new Date(b.dueDate).getTime());
  }

  clearDateFilter(): void {
    this.filterDate = null;
    this.applyFilters();
  }

  showAddProjectForm(): void {
    this.isEditing = true;
    this.editingProjectId = null;
    this.projectForm.reset({ status: 'Not Started' });
  }

  editProject(project: Project): void {
    this.isEditing = true;
    this.editingProjectId = project.id || null;
    this.projectForm.patchValue({
      name: project.name,
      description: project.description,
      dueDate: project.dueDate,
      status: project.status
    });
  }

  saveProject(): void {
    if (this.projectForm.invalid) return;
    const formValue = this.projectForm.value;
    const newProject: Project = { ...formValue };

    if (this.editingProjectId) {
      this.firestoreService.updateProject(this.editingProjectId, newProject).pipe(take(1)).subscribe(() => {
        const index = this.projects.findIndex(p => p.id === this.editingProjectId);
        if (index > -1) this.projects[index] = { ...this.projects[index], ...newProject };
        this.afterChange();
      });
    } else {
      this.firestoreService.addProject(newProject).pipe(take(1)).subscribe(docRef => {
        this.projects.push({ id: docRef.id, ...newProject });
        this.afterChange();
      });
    }
  }

  deleteProject(projectId: string): void {
    this.firestoreService.deleteProject(projectId).pipe(take(1)).subscribe(() => {
      this.projects = this.projects.filter(p => p.id !== projectId);
      this.afterChange();
    });
  }

  afterChange(): void {
    this.assigneeList = [...new Set(this.projects.map(p => p.assignee).filter((a): a is string => !!a))];
    this.applyFilters();
    this.cancelEdit();
  }

  cancelEdit(): void {
    this.isEditing = false;
    this.editingProjectId = null;
    this.projectForm.reset({ status: 'Not Started' });
  }

  toggleProjects(): void {
    this.showAllProjects = !this.showAllProjects;
  }
  loadData(): void {
    this.firestoreService.getProjects().pipe(take(1)).subscribe(projects => {
      this.projects = projects.map(project => ({
        ...project,
        dueDate: project.dueDate instanceof Date
          ? project.dueDate
          : (project.dueDate && typeof project.dueDate === 'object' && 'toDate' in project.dueDate
            ? (project.dueDate as any).toDate()
            : null)
      }));
      this.applyFilters();
    });
  }


}
<app-theme-toggle class="theme-toggle-icon"></app-theme-toggle>

<div class="projects-container">
  <h1>Projects</h1>

 <!-- Add/Edit Project Form (Styled like Filter Inputs) -->
<div class="project-form-container" *ngIf="isEditing">
  <h2>{{ editingProjectId === null ? 'Add New Project' : 'Edit Project' }}</h2>
  <form [formGroup]="projectForm" (ngSubmit)="saveProject()" class="filter-controls">

    <!-- Project Name -->
    <mat-form-field appearance="outline">
      <mat-label>Project Name</mat-label>
      <input matInput placeholder="Enter project name" formControlName="name" required />
      <mat-error *ngIf="projectForm.get('name')?.hasError('required')">Project name is required</mat-error>
    </mat-form-field>

    <!-- Assignee (as plain input) -->
    <mat-form-field appearance="outline">
      <mat-label>Assignee</mat-label>
      <input matInput placeholder="Enter assignee name" formControlName="assignee" />
    </mat-form-field>

    <!-- Due Date -->
    <mat-form-field appearance="outline">
      <mat-label>Due Date</mat-label>
      <input matInput [matDatepicker]="picker" placeholder="Pick a due date" formControlName="dueDate" required />
      <mat-datepicker-toggle matSuffix [for]="picker"></mat-datepicker-toggle>
      <mat-datepicker #picker></mat-datepicker>
      <mat-error *ngIf="projectForm.get('dueDate')?.hasError('required')">Due date is required</mat-error>
    </mat-form-field>

    <!-- Status -->
    <mat-form-field appearance="outline">
      <mat-select placeholder="Select status..." formControlName="status" required>
        <mat-option value="" disabled>Select status...</mat-option>
        <mat-option *ngFor="let status of projectStatuses" [value]="status">{{ status }}</mat-option>
      </mat-select>
      <mat-error *ngIf="projectForm.get('status')?.hasError('required')">Status is required</mat-error>
    </mat-form-field>

    <!-- Save/Cancel -->
    <div class="form-actions">
      <button mat-raised-button color="primary" type="submit" [disabled]="projectForm.invalid">Save Project</button>
      <button mat-stroked-button type="button" (click)="cancelEdit()">Cancel</button>
    </div>
  </form>
</div>

<!-- âœ… Filter Controls Re-added Below -->
<div class="filter-bar">
  <mat-form-field appearance="outline">
    <input matInput [(ngModel)]="filterProjectName" placeholder="Search by project name" (input)="applyFilters()">
  </mat-form-field>

  <mat-form-field appearance="outline">
    <input matInput [(ngModel)]="filterAssignee" placeholder="Search by assignee" (input)="applyFilters()">
  </mat-form-field>

  <mat-form-field appearance="outline">
    <input matInput [matDatepicker]="datePicker" [(ngModel)]="filterDate" placeholder="Filter by due date" (dateChange)="applyFilters()">
    <mat-datepicker-toggle matSuffix [for]="datePicker"></mat-datepicker-toggle>
    <mat-datepicker #datePicker></mat-datepicker>
  </mat-form-field>

  <mat-form-field appearance="outline">
    <mat-select [(ngModel)]="filterStatus" (selectionChange)="applyFilters()" placeholder="Select status">
      <mat-option value="">All</mat-option>
      <mat-option *ngFor="let status of projectStatuses" [value]="status">{{ status }}</mat-option>
    </mat-select>
  </mat-form-field>

  <button mat-stroked-button color="accent" (click)="clearFilters()">Reset</button>
</div>

<!-- Project Grid Display -->
<div class="projects-grid" *ngIf="filteredProjects.length > 0">
  <div class="project-card" *ngFor="let project of filteredProjects">
    <div class="project-content">
      <h2 class="project-title">{{ project.name }}</h2>
      <p class="project-description">{{ project.description }}</p>
      <p class="project-due-date" *ngIf="project.dueDate">Due: {{ project.dueDate | date:'mediumDate' }}</p>
    </div>
    <div class="project-status" [class]="project.status.toLowerCase().replace(' ', '-')">
      {{ project.status }}
    </div>
    <div class="project-actions">
      <button mat-icon-button color="primary" aria-label="Edit project" (click)="editProject(project)">
        <mat-icon>edit</mat-icon>
      </button>
      <button mat-icon-button color="warn" aria-label="Delete project" (click)="deleteProject(project.id!)">
        <mat-icon>delete</mat-icon>
      </button>
    </div>
  </div>
</div>

<div *ngIf="filteredProjects.length === 0" class="no-projects">
  No projects found matching the current filter. Try clearing the filter or adding a project!
</div>
</div>
