import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  isSidebarCollapsed = false;
  isDashboardVisible: boolean = false; // Initially, dashboard is not visible

  // Function to navigate to the dashboard
  navigateToDashboard() {
    this.isDashboardVisible = true; // Show the dashboard
  }
}
<app-header *ngIf="isDashboardVisible"></app-header> <!-- Show header only after "Get Started" -->
<app-sidebar *ngIf="isDashboardVisible"></app-sidebar> <!-- Show sidebar only after "Get Started" -->
<div class="content" *ngIf="isDashboardVisible">
  <router-outlet></router-outlet> <!-- Routed content will go here -->
</div>

<!-- Show WelcomeComponent first -->
<app-welcome *ngIf="!isDashboardVisible"></app-welcome> <!-- Show WelcomeComponent first -->
