import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatDatepickerModule } from '@angular/material/datepicker';
import { MatNativeDateModule } from '@angular/material/core';
import { MatSelectModule } from '@angular/material/select';

interface Task {
  id: number;
  name: string;
  assignee: string;
  dueDate: Date;
  status: 'Not Started' | 'In Progress' | 'Completed';
}

@Component({
  selector: 'app-tasks',
  templateUrl: './tasks.component.html',
  styleUrls: ['./tasks.component.scss'],
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    MatButtonModule,
    MatIconModule,
    MatFormFieldModule,
    MatInputModule,
    MatDatepickerModule,
    MatNativeDateModule,
    MatSelectModule
  ]
})
export class TasksComponent implements OnInit {
  tasks: Task[] = [
    { id: 1, name: 'Design new homepage', assignee: 'Alice Smith', dueDate: new Date(2024, 4, 15), status: 'In Progress' },
    { id: 2, name: 'Develop auth module', assignee: 'Bob Johnson', dueDate: new Date(2024, 4, 10), status: 'Completed' },
    { id: 3, name: 'Write blog post', assignee: 'Carol Williams', dueDate: new Date(2024, 4, 8), status: 'Not Started' },
    { id: 4, name: 'Create user testing plan', assignee: 'David Brown', dueDate: new Date(2024, 4, 20), status: 'In Progress' }
  ];

  taskForm!: FormGroup;
  isEditing = false;
  editingTaskId: number | null = null;
  nextId = 5;

  taskStatuses: string[] = ['Not Started', 'In Progress', 'Completed'];

  constructor(private fb: FormBuilder) { }

  ngOnInit(): void {
    this.taskForm = this.fb.group({
      name: ['', Validators.required],
      assignee: ['', Validators.required],
      dueDate: [null, Validators.required],
      status: ['Not Started', Validators.required]
    });
  }

  showAddTaskForm(): void {
    this.isEditing = true;
    this.editingTaskId = null;
    this.taskForm.reset({ status: 'Not Started' });
  }

  editTask(task: Task): void {
    this.isEditing = true;
    this.editingTaskId = task.id;
    this.taskForm.setValue({
      name: task.name,
      assignee: task.assignee,
      dueDate: task.dueDate,
      status: task.status
    });
  }

  saveTask(): void {
    if (this.taskForm.invalid) {
      return;
    }

    const formValue = this.taskForm.value;

    if (this.editingTaskId !== null) {
      const index = this.tasks.findIndex(t => t.id === this.editingTaskId);
      if (index > -1) {
        this.tasks[index] = { ...this.tasks[index], ...formValue };
      }
    } else {
      const newTask: Task = {
        id: this.nextId++,
        ...formValue
      };
      this.tasks.push(newTask);
    }

    this.cancelEdit();
  }

  deleteTask(taskId: number): void {
    this.tasks = this.tasks.filter(t => t.id !== taskId);
    if (this.editingTaskId === taskId) {
        this.cancelEdit();
    }
  }

  cancelEdit(): void {
    this.isEditing = false;
    this.editingTaskId = null;
    this.taskForm.reset({ status: 'Not Started' });
  }
}
