import { Component, OnInit, AfterViewInit, ElementRef, ViewChild } from '@angular/core';
import { Chart } from 'chart.js/auto';
import { CommonModule } from '@angular/common';
import { MatTableModule } from '@angular/material/table';
import { MatCardModule } from '@angular/material/card';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatSelectModule } from '@angular/material/select';
import { MatInputModule } from '@angular/material/input';
import { MatButtonModule } from '@angular/material/button';
import { MatProgressBarModule } from '@angular/material/progress-bar';

interface Task {
  id: number;
  title: string;
  status: 'Not Started' | 'In Progress' | 'Completed';
  dueDate: Date;
  assignee: string;
}

interface Project {
  id: number;
  name: string;
  description: string;
  status: 'Not Started' | 'In Progress' | 'Completed';
  dueDate: Date;
}

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss'],
  standalone: true,
  imports: [
    CommonModule,
    MatTableModule,
    MatCardModule,
    MatFormFieldModule,
    MatSelectModule,
    MatInputModule,
    MatButtonModule,
    MatProgressBarModule
  ]
})
export class DashboardComponent implements OnInit, AfterViewInit {
  @ViewChild('donutChart') donutChartRef!: ElementRef;
  @ViewChild('performanceChart') performanceChartRef!: ElementRef;

  donutChart!: Chart;
  performanceChart!: Chart;

  tasks: Task[] = [
    { id: 1, title: 'Review Design Mockups', status: 'In Progress', dueDate: new Date(2024, 3, 10), assignee: 'Alice' },
    { id: 2, title: 'Develop API Endpoint', status: 'Not Started', dueDate: new Date(2024, 3, 18), assignee: 'Bob' },
    { id: 3, title: 'Write Unit Tests', status: 'Completed', dueDate: new Date(2024, 3, 22), assignee: 'Charlie' },
    { id: 4, title: 'Deploy to Staging', status: 'Not Started', dueDate: new Date(2024, 4, 5), assignee: 'Alice' },
    { id: 5, title: 'Client Demo Prep', status: 'In Progress', dueDate: new Date(2024, 4, 12), assignee: 'Bob' },
    { id: 6, title: 'Fix Login Bug', status: 'Completed', dueDate: new Date(2024, 3, 5), assignee: 'Charlie' },
    { id: 7, title: 'Update Documentation', status: 'In Progress', dueDate: new Date(2024, 4, 20), assignee: 'Alice' },
    { id: 8, title: 'Performance Testing', status: 'Not Started', dueDate: new Date(2024, 4, 25), assignee: 'Bob' },
  ];

  projects: Project[] = [
    { id: 101, name: 'Website Redesign', description: 'Update company website', status: 'In Progress', dueDate: new Date(2024, 5, 30) },
    { id: 102, name: 'Mobile App Launch', description: 'iOS and Android app', status: 'Not Started', dueDate: new Date(2024, 6, 15) },
    { id: 103, name: 'Internal HR Portal', description: 'New portal for HR', status: 'Completed', dueDate: new Date(2024, 3, 1) },
    { id: 104, name: 'Q3 Marketing Campaign', description: 'Social media campaign', status: 'Not Started', dueDate: new Date(2024, 7, 1) },
  ];

  teamMembers: string[] = [];

  totalTasks: number = 0;
  tasksCompleted: number = 0;
  overdueTasks: number = 0;
  tasksInProgress: number = 0;
  tasksNotStarted: number = 0;

  totalProjects: number = 0;
  projectsCompleted: number = 0;
  projectsInProgress: number = 0;
  projectsNotStarted: number = 0;

  overallProgress: number = 0;

  filteredTasks: Task[] = [];
  selectedStatus = 'All';
  taskStatusLimit = 5;
  showMoreTaskStatus = true;
  displayedTaskColumns: string[] = ['title', 'status', 'assignee', 'dueDate'];

  teamPerformanceData: { member: string; tasksAssigned: number; tasksCompleted: number; completionRate: number }[] = [];
  filteredTeamData: { member: string; tasksAssigned: number; tasksCompleted: number; completionRate: number }[] = [];
  selectedTeamMember = 'All';
  teamPerformanceLimit = 5;
  showMoreTeamPerformance = true;
  displayedTeamColumns: string[] = ['member', 'tasksAssigned', 'tasksCompleted', 'completionRate'];

  upcomingDeadlines: { name: string; deadline: Date; type: 'Task' | 'Project' }[] = [];
  filteredDeadlines: { name: string; deadline: Date; type: 'Task' | 'Project' }[] = [];
  selectedDeadlineRange = 'Next 7 Days';
  upcomingDeadlinesLimit = 5;
  showMoreUpcomingDeadlines = true;
  displayedDeadlineColumns: string[] = ['name', 'deadline', 'type'];

  donutChartData: number[] = [];
  donutChartLabels: string[] = ['Completed', 'In Progress', 'Not Started'];

  performanceChartData: number[] = [1, 3, 2, 5];
  performanceChartLabels: string[] = ['Week 1', 'Week 2', 'Week 3', 'Week 4'];

  currentProjectName: string = 'N/A';
  currentProjectProgress: number = 0;
  currentProjectDaysRemaining: number = 0;

  constructor() {}

  ngOnInit(): void {
    this.calculateAllSummaries();
    this.prepareTableData();
    this.prepareChartData();
    this.prepareProjectOverview();
  }

  ngAfterViewInit() {
    this.initializeDonutChart();
    this.initializePerformanceChart();
  }

  calculateAllSummaries(): void {
    const now = new Date();

    this.totalTasks = this.tasks.length;
    this.tasksCompleted = this.tasks.filter(t => t.status === 'Completed').length;
    this.tasksInProgress = this.tasks.filter(t => t.status === 'In Progress').length;
    this.tasksNotStarted = this.tasks.filter(t => t.status === 'Not Started').length;
    this.overdueTasks = this.tasks.filter(t => t.status !== 'Completed' && t.dueDate < now).length;
    this.overallProgress = this.totalTasks > 0 ? Math.round((this.tasksCompleted / this.totalTasks) * 100) : 0;

    this.totalProjects = this.projects.length;
    this.projectsCompleted = this.projects.filter(p => p.status === 'Completed').length;
    this.projectsInProgress = this.projects.filter(p => p.status === 'In Progress').length;
    this.projectsNotStarted = this.projects.filter(p => p.status === 'Not Started').length;

    this.teamMembers = [...new Set(this.tasks.map(t => t.assignee))].sort();
  }

  prepareTableData(): void {
    this.filteredTasks = [...this.tasks].sort((a, b) => a.dueDate.getTime() - b.dueDate.getTime());

    this.teamPerformanceData = this.teamMembers.map(member => {
      const assigned = this.tasks.filter(t => t.assignee === member);
      const completed = assigned.filter(t => t.status === 'Completed');
      const rate = assigned.length > 0 ? Math.round((completed.length / assigned.length) * 100) : 0;
      return { member: member, tasksAssigned: assigned.length, tasksCompleted: completed.length, completionRate: rate };
    }).sort((a, b) => b.completionRate - a.completionRate);
    this.filteredTeamData = [...this.teamPerformanceData];

    const taskDeadlines = this.tasks.map(t => ({ name: t.title, deadline: t.dueDate, type: 'Task' as const }));
    const projectDeadlines = this.projects.map(p => ({ name: p.name, deadline: p.dueDate, type: 'Project' as const }));
    this.upcomingDeadlines = [...taskDeadlines, ...projectDeadlines]
      .filter(d => d.deadline >= new Date())
      .sort((a, b) => a.deadline.getTime() - b.deadline.getTime());
    this.filteredDeadlines = [...this.upcomingDeadlines];
    this.filterDeadlines(this.selectedDeadlineRange);
  }

  prepareChartData(): void {
    this.donutChartData = [
      this.tasksCompleted,
      this.tasksInProgress,
      this.tasksNotStarted
    ];
  }

  prepareProjectOverview(): void {
    const currentProject = this.projects.find(p => p.status === 'In Progress');
    if (currentProject) {
      this.currentProjectName = currentProject.name;
      this.currentProjectProgress = 65;
      const timeDiff = currentProject.dueDate.getTime() - new Date().getTime();
      this.currentProjectDaysRemaining = timeDiff > 0 ? Math.ceil(timeDiff / (1000 * 3600 * 24)) : 0;
    } else {
      this.currentProjectName = 'No Active Project';
      this.currentProjectProgress = 0;
      this.currentProjectDaysRemaining = 0;
    }
  }

  filterTasks(status: string): void {
    this.selectedStatus = status;
    if (status === 'All') {
      this.filteredTasks = [...this.tasks].sort((a, b) => a.dueDate.getTime() - b.dueDate.getTime());
    } else {
      this.filteredTasks = this.tasks.filter(task => task.status === status)
        .sort((a, b) => a.dueDate.getTime() - b.dueDate.getTime());
    }
    this.showMoreTaskStatus = this.filteredTasks.length > this.taskStatusLimit;
  }

  filterTeamMember(member: string): void {
    this.selectedTeamMember = member;
    if (member === 'All') {
      this.filteredTeamData = [...this.teamPerformanceData];
    } else {
      this.filteredTeamData = this.teamPerformanceData.filter(data => data.member === member);
    }
    this.showMoreTeamPerformance = this.filteredTeamData.length > this.teamPerformanceLimit;
  }

  filterDeadlines(range: string): void {
    this.selectedDeadlineRange = range;
    const now = new Date();
    const today = new Date(now.getFullYear(), now.getMonth(), now.getDate());
    let endDate = new Date(today);

    if (range === 'Next 7 Days') {
      endDate.setDate(today.getDate() + 7);
    } else if (range === 'Next 30 Days') {
      endDate.setDate(today.getDate() + 30);
    } else if (range === 'All') {
      this.filteredDeadlines = [...this.upcomingDeadlines];
      this.showMoreUpcomingDeadlines = this.filteredDeadlines.length > this.upcomingDeadlinesLimit;
      return;
    }

    this.filteredDeadlines = this.upcomingDeadlines.filter(d => d.deadline >= today && d.deadline < endDate);
    this.showMoreUpcomingDeadlines = this.filteredDeadlines.length > this.upcomingDeadlinesLimit;
  }

  loadMoreTaskStatus() { this.taskStatusLimit = this.filteredTasks.length; this.showMoreTaskStatus = false; }
  showLessTaskStatus() { this.taskStatusLimit = 5; this.showMoreTaskStatus = true; }

  loadMoreTeamPerformance() { this.teamPerformanceLimit = this.filteredTeamData.length; this.showMoreTeamPerformance = false; }
  showLessTeamPerformance() { this.teamPerformanceLimit = 5; this.showMoreTeamPerformance = true; }

  loadMoreUpcomingDeadlines() { this.upcomingDeadlinesLimit = this.filteredDeadlines.length; this.showMoreUpcomingDeadlines = false; }
  showLessUpcomingDeadlines() { this.upcomingDeadlinesLimit = 5; this.showMoreUpcomingDeadlines = true; }

  private initializeDonutChart() {
    if (!this.donutChartRef) return;
    const canvas = this.donutChartRef.nativeElement;
    const ctx = canvas.getContext('2d');

    if (this.donutChart) {
      this.donutChart.destroy();
    }

    this.donutChart = new Chart(ctx, {
      type: 'doughnut',
      data: {
        labels: this.donutChartLabels,
        datasets: [{
          data: this.donutChartData,
          backgroundColor: [
            'rgba(46, 213, 115, 0.8)',
            'rgba(255, 165, 0, 0.8)',
            'rgba(108, 117, 125, 0.8)'
          ],
          borderColor: [
            'rgba(46, 213, 115, 1)',
            'rgba(255, 165, 0, 1)',
            'rgba(108, 117, 125, 1)'
          ],
          borderWidth: 1
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        cutout: '70%',
        plugins: {
          legend: {
            position: 'bottom',
            labels: {
              color: '#ffffff'
            }
          }
        }
      }
    });
  }

  private initializePerformanceChart() {
    if (!this.performanceChartRef) return;
    const canvas = this.performanceChartRef.nativeElement;
    const ctx = canvas.getContext('2d');

    if (this.performanceChart) {
      this.performanceChart.destroy();
    }

    this.performanceChart = new Chart(ctx, {
      type: 'line',
      data: {
        labels: this.performanceChartLabels,
        datasets: [{
          label: 'Tasks Completed',
          data: this.performanceChartData,
          fill: true,
          borderColor: 'rgb(75, 192, 192)',
          backgroundColor: 'rgba(75, 192, 192, 0.2)',
          tension: 0.1
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
          y: {
            beginAtZero: true,
            ticks: {
              color: '#a0aec0'
            },
            grid: {
              color: 'rgba(255, 255, 255, 0.1)'
            }
          },
          x: {
            ticks: {
              color: '#a0aec0'
            },
            grid: {
              color: 'rgba(255, 255, 255, 0.1)'
            }
          }
        },
        plugins: {
          legend: {
            display: true,
            labels: {
              color: '#ffffff'
            }
          }
        }
      }
    });
  }
}
