import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']  // This is correct for component-specific styles
})
export class AppComponent {
  isDashboardVisible = false;

  showDashboard() {
    this.isDashboardVisible = true;
  }
}
<app-header></app-header> <!-- The header -->
<div class="app-container">
  <app-sidebar></app-sidebar> <!-- Sidebar (toggles visibility) -->
  <div class="content">
    <router-outlet></router-outlet> <!-- Routed content will go here -->
  </div>
</div>
