import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { TaskDataService } from '../task-data.service';

@Component({
  selector: 'app-task-detail',
  templateUrl: './task-detail.component.html',
  styleUrls: ['./task-detail.component.scss']
})
export class TaskDetailComponent implements OnInit {

  task: any;

  constructor(private route: ActivatedRoute, private taskDataService: TaskDataService) {}

  ngOnInit(): void {
    const taskTitle = this.route.snapshot.paramMap.get('taskTitle');
    this.task = this.taskDataService.getTasks().find(t => t.taskTitle === taskTitle);
  }
}
