import { Injectable } from '@angular/core';
import {
  Firestore,
  collection,
  addDoc,
  updateDoc,
  deleteDoc,
  doc,
  getDocs
} from '@angular/fire/firestore';
import { Task } from '../models/task.model';
import { Project } from '../models/project.model';
import { from, Observable } from 'rxjs';
import { switchMap } from 'rxjs/operators'; // ✅ ADD THIS


@Injectable({ providedIn: 'root' })
export class FirestoreService {
  constructor(private firestore: Firestore) {}

  // ----- Tasks -----
  addTask(task: Task): Observable<any> {
    return from(addDoc(collection(this.firestore, 'tasks'), task));
  }

  updateTask(taskId: string, updates: Partial<Task>): Observable<any> {
    return from(updateDoc(doc(this.firestore, `tasks/${taskId}`), updates));
  }

  deleteTask(taskId: string): Observable<any> {
    return from(deleteDoc(doc(this.firestore, `tasks/${taskId}`)));
  }

  getTasks(): Observable<Task[]> {
    return from(getDocs(collection(this.firestore, 'tasks'))).pipe(
      switchMap((snapshot: any) => {
        const tasks = snapshot.docs.map((docSnap: any) => ({
          id: docSnap.id,
          ...docSnap.data()
        }) as Task);
        return of(tasks);
      })
    );
  }


  // ----- Projects -----
  addProject(project: Project): Observable<any> {
    return from(addDoc(collection(this.firestore, 'projects'), project));
  }

  updateProject(projectId: string, updates: Partial<Project>): Observable<any> {
    return from(updateDoc(doc(this.firestore, `projects/${projectId}`), updates));
  }

  deleteProject(projectId: string): Observable<any> {
    return from(deleteDoc(doc(this.firestore, `projects/${projectId}`)));
  }

  getProjects(): Observable<Project[]> {
    return from(getDocs(collection(this.firestore, 'projects'))).pipe(
      switchMap(snapshot => {
        const projects = snapshot.docs.map(docSnap => ({
          id: docSnap.id,
          ...docSnap.data()
        }) as Project);
        return [projects];
      })
    );
  }
}
