import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

interface Task {
  id: number;
  title: string;
  status: 'not-started' | 'in-progress' | 'completed';
  dueDate: Date;
  assignee: string;
}

// Add Project interface
interface Project {
  name: string;
  description: string;
  status: 'In Progress' | 'Completed' | 'Not Started';
  dueDate: Date;
}

@Component({
  selector: 'app-calendar',
  templateUrl: './calendar.component.html',
  styleUrls: ['./calendar.component.scss'],
  standalone: true,
  imports: [CommonModule]
})
export class CalendarComponent implements OnInit {
  currentDate = new Date();
  currentMonth: number;
  currentYear: number;
  calendar: (Date | null)[][] = [];
  tasks: Task[] = [];
  projects: Project[] = []; // Add projects array

  constructor() {
    this.currentMonth = this.currentDate.getMonth();
    this.currentYear = this.currentDate.getFullYear();
  }

  ngOnInit() {
    this.generateCalendar();
    // Mock tasks
    this.tasks = [
      { id: 1, title: 'Task 1', status: 'in-progress', dueDate: new Date(2024, 3, 15), assignee: 'John Doe' },
      { id: 2, title: 'Task 2', status: 'not-started', dueDate: new Date(2024, 3, 20), assignee: 'Jane Smith' }
    ];
    // Mock projects - replace with actual data fetching
    this.projects = [
      { name: 'Website Redesign', description: 'Update the corporate website', status: 'In Progress', dueDate: new Date(2024, 3, 30) },
      { name: 'UI/UX Improvements', description: 'Improve the user experience', status: 'In Progress', dueDate: new Date(2024, 3, 25) }
    ];
  }

  generateCalendar() {
    const firstDay = new Date(this.currentYear, this.currentMonth, 1);
    const lastDay = new Date(this.currentYear, this.currentMonth + 1, 0);
    const weeks: (Date | null)[][] = [];
    let currentWeek: (Date | null)[] = [];
    const startingDay = firstDay.getDay();
    for (let i = 0; i < startingDay; i++) { currentWeek.push(null); }
    for (let day = 1; day <= lastDay.getDate(); day++) {
      if (currentWeek.length === 7) { weeks.push(currentWeek); currentWeek = []; }
      currentWeek.push(new Date(this.currentYear, this.currentMonth, day));
    }
    while (currentWeek.length < 7) { currentWeek.push(null); }
    weeks.push(currentWeek);
    this.calendar = weeks;
  }

  getTasksForDate(date: Date | null): Task[] {
    if (!date) return [];
    return this.tasks.filter(task => {
      const taskDate = new Date(task.dueDate);
      return taskDate.getDate() === date.getDate() &&
             taskDate.getMonth() === date.getMonth() &&
             taskDate.getFullYear() === date.getFullYear();
    });
  }

  // Add method to get projects for a specific date
  getProjectsForDate(date: Date | null): Project[] {
    if (!date) return [];
    return this.projects.filter(project => {
      const projectDueDate = new Date(project.dueDate);
      return projectDueDate.getDate() === date.getDate() &&
             projectDueDate.getMonth() === date.getMonth() &&
             projectDueDate.getFullYear() === date.getFullYear();
    });
  }

  previousMonth() {
    if (this.currentMonth === 0) { this.currentMonth = 11; this.currentYear--; }
    else { this.currentMonth--; }
    this.generateCalendar();
  }

  nextMonth() {
    if (this.currentMonth === 11) { this.currentMonth = 0; this.currentYear++; }
    else { this.currentMonth++; }
    this.generateCalendar();
  }

  getMonthName(): string {
    return new Date(this.currentYear, this.currentMonth).toLocaleString('default', { month: 'long' });
  }
}
<div class="calendar-container">
  <div class="calendar-header">
    <h1>Calendar</h1>
    <div class="month-navigation">
      <div class="month-year">
        {{ getMonthName() }} {{ currentYear }}
      </div>
      <div class="navigation-buttons">
        <button (click)="previousMonth()" class="nav-button">
          <span>←</span>
        </button>
        <button (click)="nextMonth()" class="nav-button">
          <span>→</span>
        </button>
      </div>
    </div>
  </div>

  <div class="calendar-grid">
    <div class="weekday-header">
      <div class="weekday">Sun</div>
      <div class="weekday">Mon</div>
      <div class="weekday">Tue</div>
      <div class="weekday">Wed</div>
      <div class="weekday">Thu</div>
      <div class="weekday">Fri</div>
      <div class="weekday">Sat</div>
    </div>

    <div class="calendar-days">
      <div class="week" *ngFor="let week of calendar">
        <div class="day" *ngFor="let day of week" [class.empty]="!day">
          <ng-container *ngIf="day">
            <div class="date-number">{{ day.getDate() }}</div>
            <div class="items-container">
              <div class="item-label">Tasks:</div>
              <div class="task-item" *ngFor="let task of getTasksForDate(day)" [class]="task.status">
                {{ task.title }}
              </div>
              <div *ngIf="getTasksForDate(day).length === 0" class="no-items">No tasks</div>

              <div class="item-label">Project Deadlines:</div>
              <div class="project-deadline-item" *ngFor="let project of getProjectsForDate(day)">
                {{ project.name }}
              </div>
              <div *ngIf="getProjectsForDate(day).length === 0" class="no-items">No deadlines</div>
            </div>
          </ng-container>
        </div>
      </div>
    </div>
  </div>
</div>
