import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']  // This is correct for component-specific styles
})
export class AppComponent {
  isDashboardVisible = false;

  showDashboard() {
    this.isDashboardVisible = true;
  }
}
<!-- Welcome Section -->
<div class="welcome-container" *ngIf="!isDashboardVisible">
  <div class="welcome-message">
    <h1>Welcome to the Dashboard</h1>
    <p>Let's dive into the project dashboard</p>
    <button class="dive-btn" (click)="showDashboard()">Dive into Dashboard</button>
  </div>
</div>

<!-- Main Content Section (Initially hidden) -->
<div [ngClass]="{'hidden-content': !isDashboardVisible, 'show-content': isDashboardVisible}">
  <app-header></app-header> <!-- The header -->
  <div class="app-container">
    <app-sidebar></app-sidebar> <!-- Sidebar (toggles visibility) -->
    <div class="content">
      <router-outlet></router-outlet> <!-- Routed content will go here -->
    </div>
  </div>
</div>
