import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';

interface Task {
  id: number;
  title: string;
  status: 'not-started' | 'in-progress' | 'completed';
  dueDate: Date;
  assignee: string;
}

@Component({
  selector: 'app-calendar',
  templateUrl: './calendar.component.html',
  styleUrls: ['./calendar.component.scss'],
  standalone: true,
  imports: [CommonModule]
})
export class CalendarComponent implements OnInit {
  currentDate = new Date();
  currentMonth: number;
  currentYear: number;
  calendar: Date[][] = [];
  tasks: Task[] = []; // This will be populated with your actual tasks

  constructor() {
    this.currentMonth = this.currentDate.getMonth();
    this.currentYear = this.currentDate.getFullYear();
  }

  ngOnInit() {
    this.generateCalendar();
    // Mock tasks - replace this with your actual task data
    this.tasks = [
      {
        id: 1,
        title: 'Complete Project Dashboard',
        status: 'in-progress',
        dueDate: new Date(2024, 3, 15),
        assignee: 'John Doe'
      },
      {
        id: 2,
        title: 'Review Code',
        status: 'not-started',
        dueDate: new Date(2024, 3, 20),
        assignee: 'Jane Smith'
      }
    ];
  }

  generateCalendar() {
    const firstDay = new Date(this.currentYear, this.currentMonth, 1);
    const lastDay = new Date(this.currentYear, this.currentMonth + 1, 0);
    const weeks: Date[][] = [];

    let currentWeek: Date[] = [];
    const startingDay = firstDay.getDay();

    // Fill in empty days before the first day of the month
    for (let i = 0; i < startingDay; i++) {
      currentWeek.push(null);
    }

    // Fill in the days of the month
    for (let day = 1; day <= lastDay.getDate(); day++) {
      if (currentWeek.length === 7) {
        weeks.push(currentWeek);
        currentWeek = [];
      }
      currentWeek.push(new Date(this.currentYear, this.currentMonth, day));
    }

    // Fill in empty days after the last day of the month
    while (currentWeek.length < 7) {
      currentWeek.push(null);
    }
    weeks.push(currentWeek);

    this.calendar = weeks;
  }

  getTasksForDate(date: Date): Task[] {
    if (!date) return [];
    return this.tasks.filter(task => {
      const taskDate = new Date(task.dueDate);
      return taskDate.getDate() === date.getDate() &&
             taskDate.getMonth() === date.getMonth() &&
             taskDate.getFullYear() === date.getFullYear();
    });
  }

  previousMonth() {
    if (this.currentMonth === 0) {
      this.currentMonth = 11;
      this.currentYear--;
    } else {
      this.currentMonth--;
    }
    this.generateCalendar();
  }

  nextMonth() {
    if (this.currentMonth === 11) {
      this.currentMonth = 0;
      this.currentYear++;
    } else {
      this.currentMonth++;
    }
    this.generateCalendar();
  }

  getMonthName(): string {
    return new Date(this.currentYear, this.currentMonth).toLocaleString('default', { month: 'long' });
  }
}
