import { Component, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatToolbarModule } from '@angular/material/toolbar';
import { MatIconModule } from '@angular/material/icon';
import { MatButtonModule } from '@angular/material/button';
import { MatMenuModule } from '@angular/material/menu';
import { MatDialog, MatDialogModule } from '@angular/material/dialog';
import { RouterModule, Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';
import { User } from '@angular/fire/auth';
import { Observable } from 'rxjs';
import { ChangePasswordDialogComponent } from 'src/app/change-password-dialog/change-password-dialog.component'; // ✅ You'll create this file next
import { MatDividerModule } from '@angular/material/divider';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss'],
  standalone: true,
  imports: [
    CommonModule,
    MatToolbarModule,
    MatIconModule,
    MatButtonModule,
    MatMenuModule,
    MatDialogModule,
    RouterModule,
    MatDividerModule
  ]
})
export class HeaderComponent {
  user$: Observable<User | null>;
  private dialog = inject(MatDialog);

  constructor(
    public authService: AuthService,
    private router: Router
  ) {
    this.user$ = authService.getUser(); // Observable for the user
  }

  logout() {
    this.authService.logout().then(() => this.router.navigate(['/auth']));
  }

  openChangePasswordDialog() {
    this.dialog.open(ChangePasswordDialogComponent, {
      width: '400px'
    });
  }
}
<!-- <mat-toolbar class="header-toolbar"> -->
<div class="header-toolbar">
  <span class="spacer"></span>

  <div class="header-actions">

    <!-- User Icon or Login -->
    <ng-container *ngIf="user$ | async as user; else loginBtn">
      <button mat-icon-button [matMenuTriggerFor]="userMenu" matTooltip="User Menu">
        <mat-icon>account_circle</mat-icon>
      </button>
      <mat-menu #userMenu="matMenu">
        <button mat-menu-item class="user-info-item">
          <mat-icon class="user-icon">person</mat-icon>
          <span><strong>Name:</strong> {{ user.displayName || 'N/A' }}</span>
        </button>
        <button mat-menu-item class="user-info-item">
          <mat-icon class="user-icon">mail</mat-icon>
          <span><strong>Email:</strong> {{ user.email }}</span>
        </button>
        <button mat-menu-item class="user-info-item">
          <mat-icon class="user-icon">phone</mat-icon>
          <span><strong>Phone:</strong> {{ user.phoneNumber || 'N/A' }}</span>
        </button>

        <button mat-menu-item (click)="openChangePasswordDialog()">
          <mat-icon class="user-icon">lock</mat-icon>
          <span>Change Password</span>
        </button>
        <button mat-menu-item (click)="logout()">
          <mat-icon class="user-icon">logout</mat-icon>
          <span>Logout</span>
        </button>
      </mat-menu>

    </ng-container>

    <ng-template #loginBtn>
      <button mat-icon-button [routerLink]="['/auth']" matTooltip="Login">
        <mat-icon>login</mat-icon>
      </button>
    </ng-template>
  </div>
<!-- </mat-toolbar> -->
