import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { MatIconModule } from '@angular/material/icon'; // ✅ Import this!


@Component({
  selector: 'app-theme-toggle',
  standalone: true,
  imports: [CommonModule, MatIconModule],
  templateUrl: './theme-toggle.component.html',
  styleUrls: ['./theme-toggle.component.scss']
})
export class ThemeToggleComponent implements OnInit {
  isDarkMode = false;

  ngOnInit(): void {
    const savedTheme = localStorage.getItem('theme');
    const prefersDark = window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches;
    const defaultTheme = savedTheme || (prefersDark ? 'dark' : 'light');

    this.isDarkMode = defaultTheme === 'dark';
    this.applyTheme();
  }

  toggleTheme(): void {
    this.isDarkMode = !this.isDarkMode;
    this.applyTheme();
  }

  private applyTheme(): void {
    const theme = this.isDarkMode ? 'dark' : 'light';

    localStorage.setItem('theme', theme);
    document.documentElement.setAttribute('data-theme', theme);
    document.body.classList.toggle('dark-mode', this.isDarkMode);
    document.body.classList.toggle('light-mode', !this.isDarkMode);
  }
}
<!-- <button class="theme-toggle-btn" (click)="toggleTheme()" title="Toggle Theme" aria-label="Toggle Theme">
  <span *ngIf="isDarkMode">☀️</span>
  <span *ngIf="!isDarkMode">🌙</span>
</button> -->
<!-- theme-toggle.component.html -->
<label class="fancy-toggle">
  <input type="checkbox" [checked]="isDarkMode" (change)="toggleTheme()" />
  <span class="toggle-slider">
    <span class="icon sun">☀️</span>
    <span class="icon moon">🌙</span>
  </span>
</label>
