import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ReactiveFormsModule, FormBuilder, FormGroup, Validators } from '@angular/forms';
import { FormsModule } from '@angular/forms';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatChipsModule } from '@angular/material/chips'; // For emails

interface TeamMember {
  id: number; // Add ID
  name: string;
  role: string;
  emails: string[];
  avatarColor: string;
}

@Component({
  selector: 'app-teams',
  templateUrl: './teams.component.html',
  styleUrls: ['./teams.component.scss'],
  standalone: true, // Make standalone
  imports: [
    CommonModule,
    ReactiveFormsModule,
    FormsModule,
    MatButtonModule,
    MatIconModule,
    MatFormFieldModule,
    MatInputModule,
    MatSelectModule,
    MatChipsModule // Add
  ]
})
export class TeamsComponent implements OnInit {
  teamMembers: TeamMember[] = [
    // Added IDs
    { id: 1, name: 'Alice Smith', role: 'Designer', emails: ['alice@smith.com', 'alice.personal@email.com'], avatarColor: '#4361ee' },
    { id: 2, name: 'Bob Johnson', role: 'Developer', emails: ['bob.j@company.dev'], avatarColor: '#7209b7' },
    { id: 3, name: 'Carol Williams', role: 'Manager', emails: ['carol.w@org.net'], avatarColor: '#4cc9f0' },
    { id: 4, name: 'David Brown', role: 'Developer', emails: ['david.brown@company.dev', 'db@personal.io'], avatarColor: '#ef4444' }
  ];

  filteredTeamMembers: TeamMember[] = [];
  teamMemberForm!: FormGroup;
  isEditing = false;
  editingMemberId: number | null = null;
  nextId = 5;

  // Filter properties
  roleList: string[] = [];
  filterName: string = '';
  selectedRole: string = 'All';

  constructor(private fb: FormBuilder) { }

  ngOnInit() {
    this.teamMemberForm = this.fb.group({
      name: ['', Validators.required],
      role: ['', Validators.required],
      emails: [''], // Handled separately? Or just display?
      avatarColor: [this.getRandomColor()] // Example: Assign random color
    });
    this.updateRoleList();
    this.applyFilters(); // Initial load
    console.log('Teams component initialized');
  }

  updateRoleList(): void {
    const roles = new Set(this.teamMembers.map(member => member.role));
    this.roleList = ['All', ...Array.from(roles).sort()];
  }

  applyFilters(): void {
    let tempMembers = [...this.teamMembers];

    // Filter by Name (case-insensitive)
    if (this.filterName) {
      const filterLower = this.filterName.toLowerCase();
      tempMembers = tempMembers.filter(member => 
        member.name.toLowerCase().includes(filterLower)
      );
    }

    // Filter by Role
    if (this.selectedRole && this.selectedRole !== 'All') {
      tempMembers = tempMembers.filter(member => member.role === this.selectedRole);
    }

    this.filteredTeamMembers = tempMembers;
  }

  clearNameFilter(): void {
      this.filterName = '';
      this.applyFilters();
  }

  // --- CRUD Methods ---
  showAddMemberForm(): void {
    this.isEditing = true;
    this.editingMemberId = null;
    this.teamMemberForm.reset({ avatarColor: this.getRandomColor() });
    // TODO: Handle email input/editing if needed
  }

  editMember(member: TeamMember): void {
    this.isEditing = true;
    this.editingMemberId = member.id;
    this.teamMemberForm.patchValue({
      name: member.name,
      role: member.role,
      // emails: member.emails.join(', '), // Example: Join emails for editing?
      avatarColor: member.avatarColor
    });
     // TODO: Handle email input/editing if needed
  }

  saveMember(): void {
    if (this.teamMemberForm.invalid) {
      return;
    }
    const formValue = this.teamMemberForm.value;
    // TODO: Handle email parsing/saving if edited
    // const emails = formValue.emails?.split(',').map((e: string) => e.trim()).filter((e: string) => e) || [];

    if (this.editingMemberId !== null) {
      // Update
      const index = this.teamMembers.findIndex(m => m.id === this.editingMemberId);
      if (index > -1) {
        this.teamMembers[index] = {
          ...this.teamMembers[index], // Keep existing ID, emails etc.
          name: formValue.name,
          role: formValue.role,
          avatarColor: formValue.avatarColor 
          // emails: emails // Update emails if handled
        };
      }
    } else {
      // Add
      const newMember: TeamMember = {
        id: this.nextId++,
        name: formValue.name,
        role: formValue.role,
        avatarColor: formValue.avatarColor,
        emails: [] // Start with empty emails or parse from form
        // emails: emails
      };
      this.teamMembers.push(newMember);
    }

    this.updateRoleList();
    this.applyFilters();
    this.cancelEdit();
  }

  // Updated delete method
  deleteTeamMember(memberId: number): void { 
    this.teamMembers = this.teamMembers.filter(m => m.id !== memberId);
    if (this.editingMemberId === memberId) {
      this.cancelEdit();
    }
    this.updateRoleList();
    this.applyFilters();
  }

  cancelEdit(): void {
    this.isEditing = false;
    this.editingMemberId = null;
    this.teamMemberForm.reset({ avatarColor: this.getRandomColor() });
  }

  // Helper to get a random color for new members
  getRandomColor(): string {
    const letters = '0123456789ABCDEF';
    let color = '#';
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  }
}
