import { Component, OnInit } from '@angular/core';
import { TaskDataService } from '../data/task-data.service'; // Import the task data service
import { ActivatedRoute } from '@angular/router';
import { Task } from '../models/task.model';

@Component({
  selector: 'app-task-detail',
  templateUrl: './task-detail.component.html',
  styleUrls: ['./task-detail.component.scss']
})
export class TaskDetailComponent implements OnInit {
  task: Task | undefined;

  constructor(
    private taskDataService: TaskDataService,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    const taskTitle = this.route.snapshot.paramMap.get('taskTitle');
    if (taskTitle) {
      this.task = this.taskDataService.getTasks().find(task => task.taskTitle === taskTitle);
    }
  }
}
