import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.scss']
})
export class DashboardComponent implements OnInit {
  // Task Status Data
  taskStatus = [
    { name: 'Task 1', status: 'Completed', dueDate: new Date('2025-01-10') },
    { name: 'Task 2', status: 'In Progress', dueDate: new Date('2025-04-12') },
    { name: 'Task 3', status: 'Completed', dueDate: new Date('2025-03-15') },
    { name: 'Task 4', status: 'Completed', dueDate: new Date('2025-02-11') },
    { name: 'Task 5', status: 'Completed', dueDate: new Date('2025-01-18') },
    { name: 'Task 6', status: 'Completed', dueDate: new Date('2025-02-21') },
    { name: 'Task 7', status: 'In Progress', dueDate: new Date('2025-04-30') },
    { name: 'Task 8', status: 'Completed', dueDate: new Date('2025-03-29') },
    { name: 'Task 9', status: 'Completed', dueDate: new Date('2025-02-23') },
    { name: 'Task 10', status: 'Completed', dueDate: new Date('2025-04-06') }
  ];

  // Team Members
  teamMembers = ['John', 'Alice', 'Bob', 'Jones', 'James', 'Bobby', 'Betty', 'Veronica', 'Charles', 'Cheryl'];

  // Team Performance Data
  teamPerformance = [
    { member: 'John', tasksAssigned: 5, tasksCompleted: 4 },
    { member: 'Alice', tasksAssigned: 3, tasksCompleted: 2 },
    { member: 'Bob', tasksAssigned: 4, tasksCompleted: 3 },
    { member: 'Jones', tasksAssigned: 2, tasksCompleted: 1 },
    { member: 'James', tasksAssigned: 3, tasksCompleted: 2 },
    { member: 'Bobby', tasksAssigned: 4, tasksCompleted: 3 },
    { member: 'Betty', tasksAssigned: 3, tasksCompleted: 2 },
    { member: 'Veronica', tasksAssigned: 2, tasksCompleted: 1 },
    { member: 'Charles', tasksAssigned: 4, tasksCompleted: 3 },
    { member: 'Cheryl', tasksAssigned: 3, tasksCompleted: 2 }
  ];

  // Upcoming Deadlines
  upcomingDeadlines = [
    { name: 'Task 1', deadline: new Date('2025-04-11') },
    { name: 'Task 2', deadline: new Date('2025-04-16') },
    { name: 'Task 3', deadline: new Date('2025-04-24') },
    { name: 'Task 4', deadline: new Date('2025-04-17') },
    { name: 'Task 5', deadline: new Date('2025-04-22') },
    { name: 'Task 6', deadline: new Date('2025-04-09') },
    { name: 'Task 7', deadline: new Date('2025-04-14') },
    { name: 'Task 8', deadline: new Date('2025-04-29') },
    { name: 'Task 9', deadline: new Date('2025-04-28') },
    { name: 'Task 10', deadline: new Date('2025-04-10') }
  ];

  // Filtered Data
  filteredTasks = [...this.taskStatus];
  filteredTeamData = [...this.teamPerformance];
  filteredDeadlines = [...this.upcomingDeadlines];

  // Selected Filters
  selectedStatus = 'All';
  selectedTeamMember = 'All';
  selectedDeadline = 'All';

  // Display Limits
  taskStatusLimit = 5;
  teamPerformanceLimit = 5;
  upcomingDeadlinesLimit = 5;

  // Show More/Less Flags
  showMoreTaskStatus = true;
  showMoreTeamPerformance = true;
  showMoreUpcomingDeadlines = true;

  // Displayed Columns
  displayedColumns = ['task', 'status'];
  displayedColumnsTeam = ['member', 'tasksAssigned', 'tasksCompleted'];
  displayedColumnsDeadlines = ['task', 'deadline'];

  // Progress and Summary
  progress = 75;
  totalTasks = this.taskStatus.length;
  tasksCompleted = this.taskStatus.filter(task => task.status === 'Completed').length;
  overdueTasks = this.taskStatus.filter(task => 
    task.status !== 'Completed' && task.dueDate < new Date()
  ).length;

  constructor() { }

  ngOnInit(): void {
    this.filterTasks('All');
    this.filterTeamMember('All');
    this.filterDeadlines('All');
  }

  // Filter Methods
  filterTasks(status: string): void {
    this.selectedStatus = status;
    this.filteredTasks = status === 'All' 
      ? [...this.taskStatus] 
      : this.taskStatus.filter(task => task.status === status);
  }

  filterTeamMember(member: string): void {
    this.selectedTeamMember = member;
    this.filteredTeamData = member === 'All'
      ? [...this.teamPerformance]
      : this.teamPerformance.filter(data => data.member === member);
  }

  filterDeadlines(deadline: string): void {
    this.selectedDeadline = deadline;
    this.filteredDeadlines = deadline === 'All'
      ? [...this.upcomingDeadlines]
      : this.upcomingDeadlines.filter(task => 
          task.deadline.toISOString().split('T')[0] === deadline
        );
  }

  // Show More/Less Methods
  loadMoreTaskStatus(): void {
    this.taskStatusLimit = this.taskStatus.length;
    this.showMoreTaskStatus = false;
  }

  showLessTaskStatus(): void {
    this.taskStatusLimit = 5;
    this.showMoreTaskStatus = true;
  }

  loadMoreTeamPerformance(): void {
    this.teamPerformanceLimit = this.teamPerformance.length;
    this.showMoreTeamPerformance = false;
  }

  showLessTeamPerformance(): void {
    this.teamPerformanceLimit = 5;
    this.showMoreTeamPerformance = true;
  }

  loadMoreUpcomingDeadlines(): void {
    this.upcomingDeadlinesLimit = this.upcomingDeadlines.length;
    this.showMoreUpcomingDeadlines = false;
  }

  showLessUpcomingDeadlines(): void {
    this.upcomingDeadlinesLimit = 5;
    this.showMoreUpcomingDeadlines = true;
  }
}
