import { Component, ElementRef, ViewChild } from '@angular/core';
import { Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { MatButtonModule } from '@angular/material/button';

@Component({
  selector: 'app-welcome',
  templateUrl: './welcome.component.html',
  styleUrls: ['./welcome.component.scss'],
  standalone: true,
  imports: [CommonModule, MatButtonModule]
})
export class WelcomeComponent {
  @ViewChild('rocketFly') rocketFly!: ElementRef;

  constructor(private router: Router) {}

  goToDashboard(): void {
    this.router.navigate(['/dashboard']);
  }

  launchRocket(event: MouseEvent): void {
    const rocketInside = (event.currentTarget as HTMLElement).querySelector('.rocket-inside') as HTMLElement;
    const rocketFly = this.rocketFly.nativeElement as HTMLElement;

    if (!rocketInside || !rocketFly) return;

    // Get position of the rocket inside the button
    const rect = rocketInside.getBoundingClientRect();

    // Set the flying rocket to same position (fixed)
    rocketFly.style.left = `${rect.left}px`;
    rocketFly.style.top = `${rect.top}px`;
    rocketFly.style.display = 'inline-block';
    rocketFly.style.animation = 'rocketLaunch 1.5s forwards ease-in-out';

    // Hide the inside rocket
    rocketInside.style.visibility = 'hidden';

    // Navigate after animation ends
    setTimeout(() => {
      this.goToDashboard();
    }, 1500);
  }
}
