import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Task } from '../models/task.model'; // Import the Task model

@Component({
  selector: 'app-task-list',
  templateUrl: './task-list.component.html',
  styleUrls: ['./task-list.component.scss']
})
export class TaskListComponent implements OnInit {

  // Task Data
  tasks: Task[] = [
    { taskTitle: 'Task 1', assignedTo: 'John', status: 'Not Started', priority: 'High', dueDate: new Date('2025-04-10') },
    { taskTitle: 'Task 2', assignedTo: 'Alice', status: 'In Progress', priority: 'Medium', dueDate: new Date('2025-04-12') },
    { taskTitle: 'Task 3', assignedTo: 'Bob', status: 'Completed', priority: 'Low', dueDate: new Date('2025-04-15') },
  ];

  // New Task Form Data
  newTask: Task = {
    taskTitle: '',
    assignedTo: '',
    status: '',
    priority: '',
    dueDate: new Date()
  };

  // Filter Options
  taskStatuses = ['Not Started', 'In Progress', 'Completed'];
  priorities = ['High', 'Medium', 'Low'];
  teamMembers = ['John', 'Alice', 'Bob'];

  filterStatus = '';
  filterPriority = '';
  filterAssigned = '';
  filterDueDate: string = '';

  taskSummary = {
    notStarted: 0,
    inProgress: 0,
    completed: 0
  };

  taskDueDates = Array.from(new Set(this.tasks.map(task => task.dueDate.toLocaleDateString())));

  filteredTasks = this.tasks;

  displayedColumns: string[] = ['taskTitle', 'assignedTo', 'status', 'priority', 'dueDate', 'action'];

  constructor(private router: Router) {}

  ngOnInit(): void {
    this.updateTaskSummary();
    this.applyFilters();
  }

  updateTaskSummary() {
    this.taskSummary.notStarted = this.tasks.filter(task => task.status === 'Not Started').length;
    this.taskSummary.inProgress = this.tasks.filter(task => task.status === 'In Progress').length;
    this.taskSummary.completed = this.tasks.filter(task => task.status === 'Completed').length;
  }

  applyFilters() {
    this.filteredTasks = this.tasks.filter(task => {
      return (
        (this.filterStatus ? task.status === this.filterStatus : true) &&
        (this.filterPriority ? task.priority === this.filterPriority : true) &&
        (this.filterAssigned ? task.assignedTo === this.filterAssigned : true) &&
        (this.filterDueDate ? task.dueDate.toLocaleDateString() === new Date(this.filterDueDate).toLocaleDateString() : true)
      );
    });
  }

  goToTaskDetail(task: Task) {
    this.router.navigate(['/task-detail', task.taskTitle]);
  }

  // Add Task (Show the form to add a task)
  addTask() {
    if (this.newTask.taskTitle && this.newTask.assignedTo && this.newTask.status && this.newTask.priority) {
      this.tasks.push({ ...this.newTask, dueDate: new Date(this.newTask.dueDate) });
      this.applyFilters(); // Update filtered tasks after adding a new task
      this.newTask = { taskTitle: '', assignedTo: '', status: '', priority: '', dueDate: new Date() }; // Reset form
      alert('Task Added Successfully');
    } else {
      alert('Please fill all fields');
    }
  }

  // Edit Task (Redirect to Edit Task page)
  editTask(task: Task) {
    this.router.navigate(['/task-edit', task.taskTitle]);
  }

  // Delete Task
  deleteTask(task: Task) {
    this.tasks = this.tasks.filter(t => t !== task);
    this.applyFilters(); // Reapply filters after deletion
  }
}
<div class="task-list-container">
  <!-- Task Summary Section -->
  <div class="task-summary">
    <mat-card class="summary-card">
      <mat-card-header>
        <mat-card-title>Task Summary</mat-card-title>
        <div class="summary-details">
            <strong class="tasklist-summary-box">Not Started: {{ taskSummary.notStarted }}</strong>
            <strong class="tasklist-summary-box"> In Progress: {{ taskSummary.inProgress }}</strong>
            <strong class="tasklist-summary-box">Completed: {{ taskSummary.completed }}</strong>
          </div>
      </mat-card-header>
      <!-- <mat-card-content>
        <div class="summary-details">
          <div>
            <strong>Not Started:</strong> {{ taskSummary.notStarted }}
          </div>
          <div>
            <strong>In Progress:</strong> {{ taskSummary.inProgress }}
          </div>
          <div>
            <strong>Completed:</strong> {{ taskSummary.completed }}
          </div>
        </div>
      </mat-card-content> -->
    </mat-card>
  </div>

  <!-- Task Table Section -->
  <mat-card class="task-table">
    <mat-card-header class="task-header">
      <mat-card-title>Task List</mat-card-title>

      <mat-form-field class="filter">
        <mat-label>Status</mat-label>
        <mat-select [(value)]="filterStatus" (selectionChange)="applyFilters()">
          <mat-option value="">All Status</mat-option>
          <mat-option *ngFor="let status of taskStatuses" [value]="status">{{ status }}</mat-option>
        </mat-select>
      </mat-form-field>

      <mat-form-field class="filter">
        <mat-label>Priority</mat-label>
        <mat-select [(value)]="filterPriority" (selectionChange)="applyFilters()">
          <mat-option value="">All Priority</mat-option>
          <mat-option *ngFor="let priority of priorities" [value]="priority">{{ priority }}</mat-option>
        </mat-select>
      </mat-form-field>

      <mat-form-field class="filter">
        <mat-label>Assigned To</mat-label>
        <mat-select [(value)]="filterAssigned" (selectionChange)="applyFilters()">
          <mat-option value="">All Members</mat-option>
          <mat-option *ngFor="let member of teamMembers" [value]="member">{{ member }}</mat-option>
        </mat-select>
      </mat-form-field>

      <mat-form-field class="filter">
        <mat-label>Due Date</mat-label>
        <mat-select [(value)]="filterDueDate" (selectionChange)="applyFilters()">
          <mat-option value="">All Dates</mat-option>
          <mat-option *ngFor="let date of taskDueDates" [value]="date">{{ date }}</mat-option>
        </mat-select>
      </mat-form-field>

      <button mat-raised-button color="primary" (click)="addTask()">Add Task</button>
    </mat-card-header>

    <mat-card-content>
      <mat-table [dataSource]="filteredTasks">
        <ng-container matColumnDef="taskTitle">
          <mat-header-cell *matHeaderCellDef> Task Title </mat-header-cell>
          <mat-cell *matCellDef="let task" (click)="goToTaskDetail(task)">
            {{ task.taskTitle }}
          </mat-cell>
        </ng-container>

        <ng-container matColumnDef="assignedTo">
          <mat-header-cell *matHeaderCellDef> Assigned To </mat-header-cell>
          <mat-cell *matCellDef="let task"> {{ task.assignedTo }} </mat-cell>
        </ng-container>

        <ng-container matColumnDef="status">
          <mat-header-cell *matHeaderCellDef> Status </mat-header-cell>
          <mat-cell *matCellDef="let task"> {{ task.status }} </mat-cell>
        </ng-container>

        <ng-container matColumnDef="priority">
          <mat-header-cell *matHeaderCellDef> Priority </mat-header-cell>
          <mat-cell *matCellDef="let task"> {{ task.priority }} </mat-cell>
        </ng-container>

        <ng-container matColumnDef="dueDate">
          <mat-header-cell *matHeaderCellDef> Due Date </mat-header-cell>
          <mat-cell *matCellDef="let task"> {{ task.dueDate | date:'shortDate' }} </mat-cell>
        </ng-container>

        <ng-container matColumnDef="action">
          <mat-header-cell *matHeaderCellDef> Action </mat-header-cell>
          <mat-cell *matCellDef="let task">
            <button mat-button (click)="editTask(task)">Edit</button>
            <button mat-button (click)="deleteTask(task)">Delete</button>
          </mat-cell>
        </ng-container>

        <mat-header-row *matHeaderRowDef="displayedColumns"></mat-header-row>
        <mat-row *matRowDef="let row; columns: displayedColumns;"></mat-row>
      </mat-table>
    </mat-card-content>
  </mat-card>
</div>
