import { Component } from '@angular/core';
import { Router, NavigationEnd } from '@angular/router';
import { filter } from 'rxjs/operators';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  isSidebarCollapsed = false;
  isDashboardVisible: boolean = false;

  constructor(private router: Router) {
    // Subscribe to router events to update visibility
    this.router.events.pipe(
      filter((event): event is NavigationEnd => event instanceof NavigationEnd)
    ).subscribe((event) => {
      // Show dashboard for all routes except welcome
      this.isDashboardVisible = !event.url.includes('welcome');
    });
  }
}
<app-header></app-header> <!-- The header -->
<div class="app-container">
  <app-sidebar></app-sidebar> <!-- Sidebar (toggles visibility) -->
  <div class="content">
    <router-outlet></router-outlet> <!-- Routed content will go here -->
  </div>
</div>
